import { format, createLogger, transports } from 'winston';

const { combine, timestamp, colorize } = format;

const logger = createLogger({
  level: process.env.AUTH0_LOG || 'info',
  format: combine(
    colorize(),
    timestamp(),
    format.printf((info) => `${info.timestamp} - ${info.level}: ${info.message}`)
  ),
  transports: [new transports.Console()],
  exitOnError: false,
});

export default logger;

import path from 'path';
import { AuthenticationClient, ManagementClient } from 'auth0';
import YAMLContext from './yaml';
import DirectoryContext from './directory';

import { isDirectory } from '../utils';
import log from '../logger';
import { Config } from '../types';

const { version: packageVersion } = require('../../package.json');

const nonPrimitiveProps: (keyof Config)[] = [
  'AUTH0_KEYWORD_REPLACE_MAPPINGS',
  'AUTH0_EXCLUDED_RULES',
  'AUTH0_EXCLUDED_CLIENTS',
  'AUTH0_EXCLUDED_DATABASES',
  'AUTH0_EXCLUDED_CONNECTIONS',
  'AUTH0_EXCLUDED_RESOURCE_SERVERS',
  'AUTH0_EXCLUDED_DEFAULTS',
  'AUTH0_EXCLUDED',
  'EXCLUDED_PROPS',
  'INCLUDED_PROPS',
];

export const setupContext = async (config: Config): Promise<DirectoryContext | YAMLContext> => {
  // Validate config
  const missingParams: ('AUTH0_DOMAIN' | 'AUTH0_CLIENT_ID' | 'AUTH0_CLIENT_SECRET')[] = [];

  if (!config.AUTH0_DOMAIN) missingParams.push('AUTH0_DOMAIN');
  if (!config.AUTH0_ACCESS_TOKEN) {
    if (!config.AUTH0_CLIENT_ID) missingParams.push('AUTH0_CLIENT_ID');
    if (!config.AUTH0_CLIENT_SECRET) missingParams.push('AUTH0_CLIENT_SECRET');
  }

  if (missingParams.length > 0) {
    throw new Error(
      `The following parameters were missing. Please add them to your config.json or as an environment variable. ${JSON.stringify(
        missingParams
      )}`
    );
  }

  ((config: Config) => {
    // Detect and warn on usage of deprecated exclusion params. See: https://github.com/auth0/auth0-deploy-cli/issues/451#user-content-deprecated-exclusion-props
    const deprecatedExclusionParams: (keyof Config)[] = [
      'AUTH0_EXCLUDED_RULES',
      'AUTH0_EXCLUDED_CLIENTS',
      'AUTH0_EXCLUDED_DATABASES',
      'AUTH0_EXCLUDED_CONNECTIONS',
      'AUTH0_EXCLUDED_RESOURCE_SERVERS',
      'AUTH0_EXCLUDED_DEFAULTS',
    ];
    const usedDeprecatedParams = deprecatedExclusionParams.filter((deprecatedParam) => {
      const deprecatedConfigValue = config[deprecatedParam] as string[] | undefined;
      return !!deprecatedConfigValue && deprecatedConfigValue.length > 0;
    });
    if (usedDeprecatedParams.length > 0) {
      log.warn(
        `Usage of the ${usedDeprecatedParams.join(', ')} exclusion ${
          usedDeprecatedParams.length > 1 ? 'params are' : 'param is'
        } deprecated and may be removed from future major versions. See: https://github.com/auth0/auth0-deploy-cli/issues/451#user-content-deprecated-exclusion-props for details.`
      );
    }
  })(config);

  const accessToken = await (async (): Promise<string> => {
    if (!!config.AUTH0_ACCESS_TOKEN) return config.AUTH0_ACCESS_TOKEN;

    const authClient = new AuthenticationClient({
      domain: config.AUTH0_DOMAIN,
      clientId: config.AUTH0_CLIENT_ID,
      clientSecret: config.AUTH0_CLIENT_SECRET,
    });

    const clientCredentials = await authClient.clientCredentialsGrant({
      audience: config.AUTH0_AUDIENCE
        ? config.AUTH0_AUDIENCE
        : `https://${config.AUTH0_DOMAIN}/api/v2/`,
    });
    return clientCredentials.access_token;
  })();

  const mgmtClient = new ManagementClient({
    domain: config.AUTH0_DOMAIN,
    token: accessToken,
    retry: { maxRetries: config.AUTH0_API_MAX_RETRIES || 10, enabled: true },
    headers: {
      'User-agent': `deploy-cli/${packageVersion} (node.js/${process.version.replace('v', '')})`,
    },
  });

  const inputFile = config.AUTH0_INPUT_FILE;

  const ensureObject = (key: keyof Config, value: any): any => {
    if (typeof value === 'string') {
      try {
        return JSON.parse(value);
      } catch (e) {
        log.debug(`Cannot convert config.${key} to an object. Error: ${e.message}`);
        return value;
      }
    }

    return value;
  };

  nonPrimitiveProps.forEach((key) => {
    if (config[key]) {
      //@ts-ignore because this method of config overwriting technically functions
      config[key] = ensureObject(key, config[key]);
    }
  });

  if (typeof inputFile === 'object') {
    return new YAMLContext(config, mgmtClient);
  }

  if (isDirectory(inputFile)) {
    return new DirectoryContext(config, mgmtClient);
  }

  const ext = path.extname(inputFile);
  if (ext === '.yaml' || ext === '.yml') {
    return new YAMLContext(config, mgmtClient);
  }

  throw new Error(
    `Unable to determine context processor to load for file ${inputFile}, does it exist? `
  );
};

import fs from 'fs-extra';
import yaml from 'js-yaml';
import path from 'path';
import { loadFileAndReplaceKeywords, keywordReplace, Auth0 } from '../../tools';

import log from '../../logger';
import { isFile, toConfigFn, stripIdentifiers, formatResults, recordsSorter } from '../../utils';
import handlers, { YAMLHandler } from './handlers';
import cleanAssets from '../../readonly';
import { Assets, Config, Auth0APIClient, AssetTypes, KeywordMappings } from '../../types';

export default class YAMLContext {
  basePath: string;
  configFile: string;
  config: Config;
  mappings: KeywordMappings;
  mgmtClient: Auth0APIClient;
  assets: Assets;

  constructor(config: Config, mgmtClient) {
    this.configFile = config.AUTH0_INPUT_FILE;
    this.config = config;
    this.mappings = config.AUTH0_KEYWORD_REPLACE_MAPPINGS || {};
    this.mgmtClient = mgmtClient;

    //@ts-ignore because the assets property gets filled out throughout
    this.assets = {};
    // Get excluded rules
    this.assets.exclude = {
      rules: config.AUTH0_EXCLUDED_RULES || [],
      clients: config.AUTH0_EXCLUDED_CLIENTS || [],
      databases: config.AUTH0_EXCLUDED_DATABASES || [],
      connections: config.AUTH0_EXCLUDED_CONNECTIONS || [],
      resourceServers: config.AUTH0_EXCLUDED_RESOURCE_SERVERS || [],
      defaults: config.AUTH0_EXCLUDED_DEFAULTS || [],
    };

    this.basePath = (() => {
      if (!!config.AUTH0_BASE_PATH) return config.AUTH0_BASE_PATH;
      //@ts-ignore because this looks to be a bug, but do not want to introduce regression; more investigation needed
      return typeof configFile === 'object' ? process.cwd() : path.dirname(this.configFile);
    })();
  }

  loadFile(f) {
    let toLoad = path.join(this.basePath, f);
    if (!isFile(toLoad)) {
      // try load not relative to yaml file
      toLoad = f;
    }
    return loadFileAndReplaceKeywords(path.resolve(toLoad), this.mappings);
  }

  async load() {
    // Allow to send object/json directly
    if (typeof this.configFile === 'object') {
      this.assets = this.configFile;
    } else {
      try {
        const fPath = path.resolve(this.configFile);
        log.debug(`Loading YAML from ${fPath}`);
        Object.assign(
          this.assets,
          yaml.load(keywordReplace(fs.readFileSync(fPath, 'utf8'), this.mappings)) || {}
        );
      } catch (err) {
        log.debug(err.stack);
        throw new Error(`Problem loading ${this.configFile}\n${err}`);
      }
    }

    const excludedAssetsFiltered = Object.keys(this.assets).reduce(
      (acc: Assets, key: AssetTypes) => {
        const excludedAssetTypes = this.config.AUTH0_EXCLUDED || [];
        if (excludedAssetTypes.includes(key)) return acc;

        return {
          ...acc,
          [key]: this.assets[key],
        };
      },
      {}
    );
    this.assets = excludedAssetsFiltered;

    // Run initial schema check to ensure valid YAML
    const auth0 = new Auth0(this.mgmtClient, this.assets, toConfigFn(this.config));
    await auth0.validate();

    // Allow handlers to process the assets such as loading files etc
    await Promise.all(
      Object.entries(handlers).map(async ([name, handler]) => {
        try {
          const parsed = await handler.parse(this);
          Object.entries(parsed).forEach(([k, v]) => {
            this.assets[k] = v;
          });
        } catch (err) {
          log.debug(err.stack);
          throw new Error(`Problem deploying ${name}`);
        }
      })
    );
  }

  async dump(): Promise<void> {
    const auth0 = new Auth0(this.mgmtClient, this.assets, toConfigFn(this.config));
    log.info('Loading Auth0 Tenant Data');
    try {
      await auth0.loadAll();
      this.assets = auth0.assets;
    } catch (err) {
      const docUrl =
        'https://auth0.com/docs/deploy/deploy-cli-tool/create-and-configure-the-deploy-cli-application#modify-deploy-cli-application-scopes';
      const extraMessage = err.message.startsWith('Insufficient scope')
        ? `\nSee ${docUrl} for more information`
        : '';
      throw new Error(`Problem loading tenant data from Auth0 ${err}${extraMessage}`);
    }

    await Promise.all(
      Object.entries(handlers)
        .filter(([handlerName]: [AssetTypes, YAMLHandler<any>]) => {
          const excludedAssetTypes = this.config.AUTH0_EXCLUDED || [];
          return !excludedAssetTypes.includes(handlerName);
        })
        .map(async ([name, handler]) => {
          try {
            const data = await handler.dump(this);
            if (data) {
              if (data[name] !== null) log.info(`Exporting ${name}`);
              Object.entries(data).forEach(([k, v]) => {
                this.assets[k] = Array.isArray(v)
                  ? v.map(formatResults).sort(recordsSorter)
                  : formatResults(v);
              });
            }
          } catch (err) {
            log.debug(err.stack);
            throw new Error(`Problem exporting ${name}`);
          }
        })
    );

    // Clean known read only fields
    let cleaned = cleanAssets(this.assets, this.config);

    // Delete exclude as it's not part of the auth0 tenant config
    delete cleaned.exclude;

    // Optionally Strip identifiers
    if (!this.config.AUTH0_EXPORT_IDENTIFIERS) {
      cleaned = stripIdentifiers(auth0, cleaned);
    }

    // Write YAML File
    const raw = yaml.dump(cleaned);
    log.info(`Writing ${this.configFile}`);
    fs.writeFileSync(this.configFile, raw);
  }
}

import constants from './constants';
import deploy from './deploy';
import Auth0 from './auth0';
import { keywordReplace, loadFileAndReplaceKeywords } from './utils';

export default {
  constants,
  deploy,
  keywordReplace,
  loadFileAndReplaceKeywords,
  Auth0,
};

export { constants, deploy, keywordReplace, loadFileAndReplaceKeywords, Auth0 };

import Auth0 from './auth0';
import log from '../logger';
import { ConfigFunction } from '../configFactory';
import { Assets, Auth0APIClient } from '../types';

export default async function deploy(
  assets: Assets,
  client: Auth0APIClient,
  config: ConfigFunction
) {
  // Setup log level
  log.level = process.env.AUTH0_DEBUG === 'true' ? 'debug' : 'info';

  log.info('Getting access token for ' + config('AUTH0_CLIENT_ID') + '/' + config('AUTH0_DOMAIN'));

  const auth0 = new Auth0(client, assets, config);

  // Validate Assets
  await auth0.validate();

  // Process changes
  await auth0.processChanges();

  return auth0.handlers.reduce((accum, h) => {
    accum[h.type] = {
      deleted: h.deleted,
      created: h.created,
      updated: h.updated,
    };
    return accum;
  }, {});
}

import Ajv from 'ajv/lib/ajv';

import pagedClient from './client';
import schema from './schema';
import handlers from './handlers';

import { Assets, Auth0APIClient, BaseAuth0APIClient } from '../../types';
import APIHandler from './handlers/default';
import { ConfigFunction } from '../../configFactory';

export type Stage = 'load' | 'validate' | 'processChanges';

type StageFunction = APIHandler['load']; // Using `load` method as a template for what type stage functions resemble

function sortByOrder(toSort: APIHandler[], stage: Stage): APIHandler[] {
  const defaultOrder = 50;

  const sorted = [...toSort];
  sorted.sort((a, b) => {
    //@ts-ignore because this doesn't actually work. TODO: apply stage order
    const aOrder = a[stage].order || defaultOrder;
    //@ts-ignore because this doesn't actually work. TODO: apply stage order
    const bOrder = b[stage].order || defaultOrder;
    return aOrder - bOrder;
  });
  return sorted;
}

export default class Auth0 {
  client: Auth0APIClient;
  config: ConfigFunction;
  assets: Assets;
  handlers: APIHandler[];

  constructor(client: BaseAuth0APIClient, assets: Assets, config: ConfigFunction) {
    this.client = pagedClient(client);
    this.config = config;
    this.assets = assets;

    this.handlers = Object.values(handlers)
      .map((handler) => {
        //@ts-ignore because class expects `type` property but gets directly injected into class constructors
        return new handler.default({ client: this.client, config: this.config });
      })
      .filter((handler) => {
        const excludedAssetTypes = config('AUTH0_EXCLUDED') || [];
        return !excludedAssetTypes.includes(handler.type);
      });
  }

  async runStage(stage: Stage): Promise<void> {
    // Sort by priority
    for (const handler of sortByOrder(this.handlers, stage)) {
      // eslint-disable-line
      try {
        const stageFn: StageFunction = Object.getPrototypeOf(handler)[stage];
        this.assets = {
          ...this.assets,
          ...((await stageFn.apply(handler, [this.assets])) || {}),
        };
      } catch (err) {
        err.type = handler.type;
        err.stage = stage;
        throw err;
      }
    }
  }

  async validate(): Promise<void> {
    const ajv = new Ajv({ useDefaults: true, nullable: true });
    const nonNullAssets = Object.keys(this.assets)
      .filter((k) => this.assets[k] != null)
      .reduce((a, k) => ({ ...a, [k]: this.assets[k] }), {});
    const valid = ajv.validate(schema, nonNullAssets);
    if (!valid) {
      throw new Error(`Schema validation failed loading ${JSON.stringify(ajv.errors, null, 4)}`);
    }

    await this.runStage('validate');
  }

  async loadAll(): Promise<void> {
    // Populate assets from auth0 tenant
    await this.runStage('load');
  }

  async processChanges(): Promise<void> {
    await this.runStage('processChanges');
  }
}

import { PromisePoolExecutor } from 'promise-pool-executor';
import _ from 'lodash';

import { flatten } from '../utils';
import {
  Asset,
  ApiResponse,
  Auth0APIClient,
  CheckpointPaginationParams,
  PagePaginationParams,
  BaseAuth0APIClient,
} from '../../types';

const API_CONCURRENCY = 3;
// To ensure a complete deployment, limit the API requests generated to be 80% of the capacity
// https://auth0.com/docs/policies/rate-limits#management-api-v2
const API_FREQUENCY_PER_SECOND = 8;

const MAX_PAGE_SIZE = 100;

function getEntity(rsp: ApiResponse): Asset[] {
  const found = Object.values(rsp).filter((a) => Array.isArray(a));
  if (Array.isArray(found) && found.length === 1) {
    return found[0] as Asset[];
  }
  throw new Error('There was an error trying to find the entity within paginate');
}

function checkpointPaginator(
  client: Auth0APIClient,
  target,
  name: 'getAll'
): (arg0: CheckpointPaginationParams) => Promise<Asset[]> {
  return async function (...args: [CheckpointPaginationParams]) {
    const data: Asset[] = [];

    // remove the _checkpoint_ flag
    const { checkpoint, ...newArgs } = _.cloneDeep(args[0]);

    // fetch the total to validate records match
    const { total } = await client.pool
      .addSingleTask({
        data: newArgs,
        generator: (requestArgs) => target[name](requestArgs),
      })
      .promise();

    let done = false;
    // use checkpoint pagination to allow fetching 1000+ results
    newArgs.take = 50;

    while (!done) {
      const rsp = await client.pool
        .addSingleTask({
          data: newArgs,
          generator: (requestArgs) => target[name](requestArgs),
        })
        .promise();

      data.push(...getEntity(rsp));
      if (!rsp.next) {
        done = true;
      } else {
        newArgs.from = rsp.next;
      }
    }

    if (data.length !== total) {
      throw new Error('Fail to load data from tenant');
    }

    return data;
  };
}

function pagePaginator(
  client: Auth0APIClient,
  target,
  name: 'getAll'
): (arg0: PagePaginationParams) => Promise<Asset[]> {
  return async function (...args: [PagePaginationParams]): Promise<Asset[]> {
    // Where the entity data will be collected
    const data: Asset[] = [];

    // Create new args and inject the properties we require for pagination automation
    const newArgs = [...args];
    newArgs[0] = { ...newArgs[0], page: 0 };

    // Grab data we need from the request then delete the keys as they are only needed for this automation function to work
    const perPage = newArgs[0].per_page || MAX_PAGE_SIZE;
    newArgs[0].per_page = perPage;
    delete newArgs[0].paginate;

    // Run the first request to get the total number of entity items
    const rsp = await client.pool
      .addSingleTask({
        data: _.cloneDeep(newArgs),
        generator: (pageArgs) => target[name](...pageArgs),
      })
      .promise();

    data.push(...getEntity(rsp));
    const total = rsp.total || 0;
    const pagesLeft = Math.ceil(total / perPage) - 1;
    // Setup pool to get the rest of the pages
    if (pagesLeft > 0) {
      const pages = await client.pool
        .addEachTask({
          data: Array.from(Array(pagesLeft).keys()),
          generator: (page) => {
            const pageArgs = _.cloneDeep(newArgs);
            pageArgs[0].page = page + 1;

            return target[name](...pageArgs).then((r) => getEntity(r));
          },
        })
        .promise();

      data.push(...flatten(pages));

      if (data.length !== total) {
        throw new Error('Fail to load data from tenant');
      }
    }
    return data;
  };
}

// Warp around a <resource>Manager and detect when requesting specific pages to return all
function pagedManager(client: Auth0APIClient, manager: Auth0APIClient) {
  return new Proxy<Auth0APIClient>(manager, {
    get: function (target: Auth0APIClient, name: string, receiver: unknown) {
      if (name === 'getAll') {
        return async function (...args: [CheckpointPaginationParams | PagePaginationParams]) {
          switch (true) {
            case args[0] && typeof args[0] === 'object' && args[0].checkpoint:
              return checkpointPaginator(
                client,
                target,
                name
              )(...(args as [CheckpointPaginationParams]));
            case args[0] && typeof args[0] === 'object' && args[0].paginate:
              return pagePaginator(client, target, name)(...(args as [PagePaginationParams]));
            default:
              return target[name](...args);
          }
        };
      }

      const nestedManager = Reflect.get(target, name, receiver);

      if (typeof nestedManager === 'object' && nestedManager !== null) {
        return pagedManager(client, nestedManager);
      }

      return nestedManager;
    },
  });
}

// Warp around the ManagementClient and detect when requesting specific pages to return all
export default function pagedClient(client: BaseAuth0APIClient): Auth0APIClient {
  const clientWithPooling: Auth0APIClient = {
    ...client,
    pool: new PromisePoolExecutor({
      concurrencyLimit: API_CONCURRENCY,
      frequencyLimit: API_FREQUENCY_PER_SECOND,
      frequencyWindow: 1000, // 1 sec
    }),
  };

  return pagedManager(clientWithPooling, clientWithPooling);
}

import path from 'path';
import fs, { constants as fsConstants } from 'fs';
import dotProp from 'dot-prop';
import _ from 'lodash';
import log from '../logger';
import { Asset, Assets, CalculatedChanges, KeywordMappings } from '../types';
import constants from './constants';

export function keywordArrayReplace(input: string, mappings: KeywordMappings): string {
  Object.keys(mappings).forEach(function (key) {
    // Matching against two sets of patterns because a developer may provide their array replacement keyword with or without wrapping quotes. It is not obvious to the developer which to do depending if they're operating in YAML or JSON.
    const pattern = `@@${key}@@`;
    const patternWithQuotes = `"${pattern}"`;

    const regex = new RegExp(`${patternWithQuotes}|${pattern}`, 'g');
    input = input.replace(regex, JSON.stringify(mappings[key]));
  });
  return input;
}

export function keywordStringReplace(input: string, mappings: KeywordMappings): string {
  Object.keys(mappings).forEach(function (key) {
    const regex = new RegExp(`##${key}##`, 'g');
    // @ts-ignore TODO: come back and distinguish strings vs array replacement.
    input = input.replace(regex, mappings[key]);
  });
  return input;
}

export function keywordReplace(input: string, mappings: KeywordMappings): string {
  // Replace keywords with mappings within input.
  if (mappings && Object.keys(mappings).length > 0) {
    input = keywordArrayReplace(input, mappings);
    input = keywordStringReplace(input, mappings);
  }
  return input;
}

export function convertClientNameToId(name: string, clients: Asset[]): string {
  const found = clients.find((c) => c.name === name);
  return (found && found.client_id) || name;
}

export function convertClientNamesToIds(names: string[], clients: Asset[]): string[] {
  const resolvedNames = names.map((name) => ({ name, resolved: false }));
  const result = clients.reduce((acc: string[], client): string[] => {
    if (names.includes(client.name)) {
      const index = resolvedNames.findIndex((item) => item.name === client.name);
      resolvedNames[index].resolved = true;
      return [...acc, client.client_id];
    }
    return [...acc];
  }, []);
  const unresolved = resolvedNames.filter((item) => !item.resolved).map((item) => item.name);
  // @ts-ignore TODO: come back and refactor to use map instead of reduce.
  return [...unresolved, ...result];
}

export function loadFileAndReplaceKeywords(file: string, mappings: KeywordMappings): string {
  // Load file and replace keyword mappings
  const f = path.resolve(file);
  try {
    fs.accessSync(f, fsConstants.F_OK);
    if (mappings) {
      return keywordReplace(fs.readFileSync(f, 'utf8'), mappings);
    }
    return fs.readFileSync(f, 'utf8');
  } catch (error) {
    throw new Error(`Unable to load file ${f} due to ${error}`);
  }
}

export function flatten(list: any[]): any[] {
  // Flatten an multiple arrays to single array
  return list.reduce((a, b) => a.concat(Array.isArray(b) ? flatten(b) : b), []);
}

export function convertJsonToString(obj: { [key: string]: any }, spacing = 0): string {
  return JSON.stringify(obj, null, spacing);
}

export function stripFields(obj: Asset, fields: string[]): Asset {
  // Strip object fields supporting dot notation (ie: a.deep.field)
  const stripped: string[] = [];

  const newObj = { ...obj };
  fields.forEach((f) => {
    if (dotProp.get(newObj, f) !== undefined) {
      dotProp.delete(newObj, f);
      stripped.push(f);
    }
  });

  if (stripped) {
    const name = ['id', 'client_id', 'template', 'name'].reduce((n, k) => newObj[k] || n, '');
    log.debug(`Stripping "${name}" read-only fields ${JSON.stringify(stripped)}`);
  }
  return newObj;
}

export function getEnabledClients(
  assets: Assets,
  connection: Asset,
  existing: Asset[],
  clients: Asset[]
): string[] | undefined {
  // Convert enabled_clients by name to the id

  if (connection.enabled_clients === undefined) return undefined; // If no enabled clients passed in, explicitly ignore from management, preventing unintentional disabling of connection.

  const excludedClientsByNames = (assets.exclude && assets.exclude.clients) || [];
  const excludedClients = convertClientNamesToIds(excludedClientsByNames, clients);
  const enabledClients = [
    ...convertClientNamesToIds(connection.enabled_clients || [], clients).filter(
      (item) => ![...excludedClientsByNames, ...excludedClients].includes(item)
    ),
  ];
  // If client is excluded and in the existing connection this client is enabled, it should keep enabled
  // If client is excluded and in the existing connection this client is disabled, it should keep disabled
  existing.forEach((conn) => {
    if (conn.name === connection.name) {
      excludedClients.forEach((excludedClient) => {
        if (conn.enabled_clients.includes(excludedClient)) {
          enabledClients.push(excludedClient);
        }
      });
    }
  });
  return enabledClients;
}

export function duplicateItems(arr: Asset[], key: string): Asset[] {
  // Find duplicates objects within array that have the same key value
  const duplicates = arr.reduce(
    (accum: { [key: string]: Asset[] }, obj): { [key: string]: Asset[] } => {
      const keyValue = obj[key];
      if (keyValue) {
        if (!(keyValue in accum)) accum[keyValue] = [];
        accum[keyValue].push(obj);
      }
      return accum;
    },
    {}
  );
  return Object.values(duplicates).filter((g) => g.length > 1);
}

export function filterExcluded(changes: CalculatedChanges, exclude: string[]): CalculatedChanges {
  const { del, update, create, conflicts } = changes;

  if (!exclude.length) {
    return changes;
  }

  const filter = (list: Asset[]) => list.filter((item) => !exclude.includes(item.name));

  return {
    del: filter(del),
    update: filter(update),
    create: filter(create),
    conflicts: filter(conflicts),
  };
}

export function areArraysEquals(x: any[], y: any[]): boolean {
  return _.isEqual(x && x.sort(), y && y.sort());
}

export const obfuscateSensitiveValues = (
  data: Asset | Asset[] | null,
  sensitiveFieldsToObfuscate: string[]
): Asset | Asset[] | null => {
  if (data === null) return data;
  if (Array.isArray(data)) {
    return data.map((asset) => obfuscateSensitiveValues(asset, sensitiveFieldsToObfuscate));
  }

  const newAsset = { ...data };
  sensitiveFieldsToObfuscate.forEach((sensitiveField) => {
    if (dotProp.get(newAsset, sensitiveField) !== undefined) {
      dotProp.set(newAsset, sensitiveField, constants.OBFUSCATED_SECRET_VALUE);
    }
  });

  return newAsset;
};

// The reverse of `obfuscateSensitiveValues()`, preventing an obfuscated value from being passed to the API
export const stripObfuscatedFieldsFromPayload = (
  data: Asset | Asset[] | null,
  obfuscatedFields: string[]
): Asset | Asset[] | null => {
  if (data === null) return data;
  if (Array.isArray(data)) {
    return data.map((asset) => stripObfuscatedFieldsFromPayload(asset, obfuscatedFields));
  }

  const newAsset = { ...data };
  obfuscatedFields.forEach((sensitiveField) => {
    const obfuscatedFieldValue = dotProp.get(newAsset, sensitiveField);
    if (obfuscatedFieldValue === constants.OBFUSCATED_SECRET_VALUE) {
      dotProp.delete(newAsset, sensitiveField);
    }
  });

  return newAsset;
};

export const detectInsufficientScopeError = async <T>(
  fn: Function
): Promise<
  | {
      hadSufficientScopes: true;
      data: T;
      requiredScopes: [];
    }
  | { hadSufficientScopes: false; requiredScopes: string[]; data: null }
> => {
  try {
    const data = await fn();
    return {
      hadSufficientScopes: true,
      data,
      requiredScopes: [],
    };
  } catch (err) {
    if (err.statusCode === 403 && err.message.includes('Insufficient scope')) {
      const requiredScopes = err.message?.split('Insufficient scope, expected any of: ')?.slice(1);
      return {
        hadSufficientScopes: false,
        requiredScopes,
        data: null,
      };
    }
    throw err;
  }
};

import handlers from './handlers';

const typesSchema = Object.entries(handlers).reduce(
  (map: { [key: string]: Object }, [name, obj]) => {
    map[name] = obj.schema; //eslint-disable-line
    return map;
  },
  {}
);

const excludeSchema = Object.entries(handlers).reduce(
  (map: { [key: string]: Object }, [name, obj]) => {
    if (obj.excludeSchema) {
      map[name] = obj.excludeSchema;
    }
    return map;
  },
  {}
);

export default {
  type: 'object',
  $schema: 'http://json-schema.org/draft-07/schema#',
  properties: {
    ...typesSchema,
    exclude: {
      type: 'object',
      properties: { ...excludeSchema },
      default: {},
    },
  },
  additionalProperties: false,
};

