        default: 'login_success',
        enum: ['login_success', 'login_failure', 'pre_authorize'],
      },
    },
    required: ['name'],
  },
};

export default class RulesHandler extends DefaultHandler {
  existing: Asset[];

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'rules',
      stripUpdateFields: ['stage'], // Fields not allowed in updates
    });
  }

  async getType(): Promise<Asset[]> {
    if (this.existing) return this.existing;
    this.existing = await this.client.rules.getAll({ paginate: true, include_totals: true });
    return this.existing;
  }

  objString(rule): string {
    return super.objString({ name: rule.name, order: rule.order });
  }

  async calcChanges(
    assets,
    includeExcluded = false
  ): Promise<CalculatedChanges & { reOrder: Asset[] }> {
    let { rules } = assets;

    const excludedRules = (assets.exclude && assets.exclude.rules) || [];

    let existing = await this.getType();

    // Filter excluded rules
    if (!includeExcluded) {
      rules = rules.filter((r) => !excludedRules.includes(r.name));
      existing = existing.filter((r) => !excludedRules.includes(r.name));
    }

    // Figure out what needs to be updated vs created
    const { del, update, create, conflicts } = calculateChanges({
      handler: this,
      assets: rules,
      existing,
      identifiers: ['id', 'name'],
      allowDelete: !!this.config('AUTH0_ALLOW_DELETE'),
    });
    // Figure out the rules that need to be re-ordered
    const futureRules = [...create, ...update];

    const futureMaxOrder = Math.max(...futureRules.map((r) => r.order));
    const existingMaxOrder = Math.max(...existing.map((r) => r.order));
    let nextOrderNo = Math.max(futureMaxOrder, existingMaxOrder);

    //@ts-ignore because we know reOrder is Asset[]
    const reOrder: Asset[] = futureRules.reduce((accum: Asset[], r: Asset) => {
      const conflict = existing.find((f) => r.order === f.order && r.name !== f.name);
      if (conflict !== undefined) {
        nextOrderNo += 1;
        return [
          ...accum,
          {
            ...conflict,
            order: nextOrderNo,
          },
        ];
      }
      return accum;
    }, []);

    return {
      del,
      update,
      create,
      reOrder,
      conflicts,
    };
  }

  async validate(assets: Assets): Promise<void> {
    const { rules } = assets;

    // Do nothing if not set
    if (!rules) return;

    const excludedRules = (assets.exclude && assets.exclude.rules) || [];

    // Figure out what needs to be updated vs created
    const { update, create, del } = await this.calcChanges(assets, true);
    // Include del rules which are actually not going to be deleted but are excluded
    // they can still muck up the ordering so we must take it into consideration.
    const futureRules = [
      ...create,
      ...update,
      ...del.filter((r) => excludedRules.includes(r.name)),
    ];

    // Detect rules with the same order
    const rulesSameOrder = duplicateItems(futureRules, 'order');
    if (rulesSameOrder.length > 0) {
      const formatted = rulesSameOrder.map((dups) => dups.map((d) => `${d.name}`));
      throw new ValidationError(`There are multiple rules for the following stage-order combinations
      ${convertJsonToString(formatted)}.
       Only one rule must be defined for the same order number in a stage.`);
    }

    // Detect Rules that are changing stage as it's not allowed.
    const existing = await this.getType();
    const stateChanged = futureRules
      .reduce(
        (changed: Asset[], rule) => [
          ...changed,
          ...existing.filter(
            (r) => rule.name.toLowerCase() === r.name.toLowerCase() && r.stage !== rule.stage
          ),
        ],
        []
      )
      .map((r) => r.name);

    if (stateChanged.length > 0) {
      throw new ValidationError(`The following rules changed stage which is not allowed:
      ${convertJsonToString(stateChanged)}.
      Rename the rules to recreate them and avoid this error.`);
    }

    await super.validate(assets);
  }

  async processChanges(assets: Assets): Promise<void> {
    const { rules } = assets;

    // Do nothing if not set
    if (!rules) return;

    // Figure out what needs to be updated vs created
    const changes = await this.calcChanges(assets);

    // Temporally re-order rules with conflicting ordering
    await this.client.pool
      .addEachTask({
        data: changes.reOrder,
        generator: (rule) =>
          this.client.rules
            .update({ id: rule.id }, stripFields(rule, this.stripUpdateFields))
            .then(() => {
              const updated = {
                name: rule.name,
                stage: rule.stage,
                order: rule.order,
                id: rule.id,
              };
              log.info(`Temporally re-order Rule ${convertJsonToString(updated)}`);
            }),
      })
      .promise();

    await super.processChanges(assets, {
      del: changes.del,
      create: changes.create,
      update: changes.update,
      conflicts: changes.conflicts,
    });
  }
}

function ValidationError(message: string) {
  Error.call(this, message);
  if (process.env.NODE_ENV !== 'production') {
    Error.captureStackTrace(this, this.constructor);
  }
  this.name = 'ValidationError';
  this.message = message;
  this.status = 400;
}

ValidationError.prototype = Object.create(Error.prototype);
ValidationError.prototype.constructor = ValidationError;

export default ValidationError;

import ValidationError from '../../validationError';

import {
  stripFields,
  convertJsonToString,
  duplicateItems,
  obfuscateSensitiveValues,
  stripObfuscatedFieldsFromPayload,
  detectInsufficientScopeError,
} from '../../utils';
import log from '../../../logger';
import { calculateChanges } from '../../calculateChanges';
import { Asset, Assets, Auth0APIClient, CalculatedChanges } from '../../../types';
import { ConfigFunction } from '../../../configFactory';

export function order(value) {
  return function decorator(t, n, descriptor) {
    descriptor.value.order = value; // eslint-disable-line
    return descriptor;
  };
}

type ApiMethodOverride = string | Function;

export default class APIHandler {
  config: ConfigFunction;
  id: string;
  type: string;
  updated: number;
  created: number;
  deleted: number;
  existing: null | Asset | Asset[];
  client: Auth0APIClient; // TODO: apply stronger types to Auth0 API client
  identifiers: string[];
  objectFields: string[];
  sensitiveFieldsToObfuscate: string[];
  stripUpdateFields: string[]; //Fields to strip from payload when updating
  stripCreateFields: string[]; //Fields to strip from payload when creating
  name?: string; // TODO: understand if any handlers actually leverage `name` property
  functions: {
    getAll: ApiMethodOverride;
    update: ApiMethodOverride;
    create: ApiMethodOverride;
    delete: ApiMethodOverride;
  };

  constructor(options: {
    id?: APIHandler['id'];
    config: ConfigFunction;
    type: APIHandler['type'];
    client: Auth0APIClient;
    objectFields?: APIHandler['objectFields'];
    identifiers?: APIHandler['identifiers'];
    stripUpdateFields?: APIHandler['stripUpdateFields'];
    sensitiveFieldsToObfuscate?: APIHandler['sensitiveFieldsToObfuscate'];
    stripCreateFields?: APIHandler['stripCreateFields'];
    functions: {
      getAll?: ApiMethodOverride;
      update?: ApiMethodOverride;
      create?: ApiMethodOverride;
      delete?: ApiMethodOverride;
    };
  }) {
    this.config = options.config;
    this.type = options.type;
    this.id = options.id || 'id';
    this.client = options.client;
    this.existing = null;
    this.identifiers = options.identifiers || ['id', 'name'];
    this.objectFields = options.objectFields || [];
    this.stripUpdateFields = [...(options.stripUpdateFields || []), this.id];
    this.sensitiveFieldsToObfuscate = options.sensitiveFieldsToObfuscate || [];
    this.stripCreateFields = options.stripCreateFields || [];

    this.functions = {
      getAll: 'getAll',
      create: 'create',
      delete: 'delete',
      update: 'update',
      ...(options.functions || {}),
    };

    this.updated = 0;
    this.created = 0;
    this.deleted = 0;
  }

  getClientFN(fn: ApiMethodOverride): Function {
    if (typeof fn === 'string') {
      const client = this.client[this.type];
      return client[fn].bind(client);
    }
    return fn;
  }

  didDelete(item: Asset): void {
    log.info(`Deleted [${this.type}]: ${this.objString(item)}`);
  }

  didCreate(item: Asset): void {
    log.info(`Created [${this.type}]: ${this.objString(item)}`);
  }

  didUpdate(item: Asset): void {
    log.info(`Updated [${this.type}]: ${this.objString(item)}`);
  }

  objString(item: Asset): string {
    return convertJsonToString(item);
  }

  async getType(): Promise<Asset | Asset[] | null> {
    // Each type to impl how to get the existing as its not consistent across the mgnt api.
    throw new Error(`Must implement getType for type ${this.type}`);
  }

  async load(): Promise<{ [key: string]: Asset | Asset[] | null }> {
    // Load Asset from Tenant
    const data = await (async () => {
      const { data, hadSufficientScopes, requiredScopes } = await detectInsufficientScopeError<
        Asset | Asset[]
      >(this.getType.bind(this));
      if (!hadSufficientScopes) {
        log.warn(`Cannot retrieve ${this.type} due to missing scopes: ${requiredScopes}`);
        return null;
      }
      log.info(`Retrieving ${this.type} data from Auth0`);
      return data;
    })();

    this.existing = obfuscateSensitiveValues(data, this.sensitiveFieldsToObfuscate);

    return { [this.type]: this.existing };
  }

  async calcChanges(assets: Assets): Promise<CalculatedChanges> {
    const typeAssets = assets[this.type];

    // Do nothing if not set
    if (!typeAssets) {
      return {
        del: [],
        create: [],
        conflicts: [],
        update: [],
      };
    }

    const existing = await this.getType();

    // Figure out what needs to be updated vs created
    return calculateChanges({
      handler: this,
      assets: typeAssets,
      allowDelete: !!this.config('AUTH0_ALLOW_DELETE'),
      //@ts-ignore TODO: investigate what happens when `existing` is null
      existing,
      identifiers: this.identifiers,
    });
  }

  async validate(assets: Assets): Promise<void> {
    // Ensure no duplication in id and name
    const typeAssets = assets[this.type];

    // Do nothing if not set
    if (!Array.isArray(typeAssets)) return;

    // Do not allow items with same name
    const duplicateNames = duplicateItems(typeAssets, 'name');
    if (duplicateNames.length > 0) {
      const formatted = duplicateNames.map((dups) => dups.map((d) => `${d.name}`));
      throw new ValidationError(`There are multiple ${this.type} with the same name combinations
      ${convertJsonToString(formatted)}.
       Names must be unique.`);
    }

    // Do not allow items with same id
    const duplicateIDs = duplicateItems(typeAssets, this.id);
    if (duplicateIDs.length > 0) {
      const formatted = duplicateIDs.map((dups) => dups.map((d) => `${d[this.id]}`));
      throw new ValidationError(`There are multiple ${
        this.type
      } for the following stage-order combinations
      ${convertJsonToString(formatted)}.
       Only one rule must be defined for the same order number in a stage.`);
    }
  }

  async processChanges(assets: Assets, changes: CalculatedChanges): Promise<void> {
    if (!changes) {
      changes = await this.calcChanges(assets);
    }

    const del = changes.del || [];
    const update = changes.update || [];
    const create = changes.create || [];
    const conflicts = changes.conflicts || [];

    log.debug(
      `Start processChanges for ${this.type} [delete:${del.length}] [update:${update.length}], [create:${create.length}], [conflicts:${conflicts.length}]`
    );

    // Process Deleted
    if (del.length > 0) {
      const allowDelete =
        this.config('AUTH0_ALLOW_DELETE') === 'true' || this.config('AUTH0_ALLOW_DELETE') === true;
      const byExtension =
        this.config('EXTENSION_SECRET') &&
        (this.type === 'rules' || this.type === 'resourceServers');
      const shouldDelete = allowDelete || byExtension;
      if (!shouldDelete) {
        log.warn(`Detected the following ${
          this.type
        } should be deleted. Doing so may be destructive.\nYou can enable deletes by setting 'AUTH0_ALLOW_DELETE' to true in the config
        \n${changes.del.map((i) => this.objString(i)).join('\n')}
         `);
      } else {
        await this.client.pool
          .addEachTask({
            data: del || [],
            generator: (delItem) => {
              const delFunction = this.getClientFN(this.functions.delete);
              return delFunction({ [this.id]: delItem[this.id] })
                .then(() => {
                  this.didDelete(delItem);
                  this.deleted += 1;
                })
                .catch((err) => {
                  throw new Error(
                    `Problem deleting ${this.type} ${this.objString(delItem)}\n${err}`
                  );
                });
            },
          })
          .promise();
      }
    }

    // Process Renaming Entries Temp due to conflicts in names
    await this.client.pool
      .addEachTask({
        data: conflicts || [],
        generator: (updateItem) => {
          const updateFN = this.getClientFN(this.functions.update);
          const params = { [this.id]: updateItem[this.id] };
          const updatePayload = (() => {
            let data = stripFields({ ...updateItem }, this.stripUpdateFields);
            return stripObfuscatedFieldsFromPayload(data, this.sensitiveFieldsToObfuscate);
          })();
          return updateFN(params, updatePayload)
            .then((data) => this.didUpdate(data))
            .catch((err) => {
              throw new Error(
                `Problem updating ${this.type} ${this.objString(updateItem)}\n${err}`
              );
            });
        },
      })
      .promise();

    // Process Creations
    await this.client.pool
      .addEachTask({
        data: create || [],
        generator: (createItem) => {
          const createFunction = this.getClientFN(this.functions.create);
          const createPayload = (() => {
            const strippedPayload = stripFields(createItem, this.stripCreateFields);
            return stripObfuscatedFieldsFromPayload(
              strippedPayload,
              this.sensitiveFieldsToObfuscate
            );
          })();
          return createFunction(createPayload)
            .then((data) => {
              this.didCreate(data);
              this.created += 1;
            })
            .catch((err) => {
              throw new Error(
                `Problem creating ${this.type} ${this.objString(createItem)}\n${err}`
              );
            });
        },
      })
      .promise();

    // Process Updates and strip fields not allowed in updates
    await this.client.pool
      .addEachTask({
        data: update || [],
        generator: (updateItem) => {
          const updateFN = this.getClientFN(this.functions.update);
          const params = { [this.id]: updateItem[this.id] };
          const updatePayload = (() => {
            let data = stripFields({ ...updateItem }, this.stripUpdateFields);
            return stripObfuscatedFieldsFromPayload(data, this.sensitiveFieldsToObfuscate);
          })();
          return updateFN(params, updatePayload)
            .then((data) => {
              this.didUpdate(data);
              this.updated += 1;
            })
            .catch((err) => {
              throw new Error(
                `Problem updating ${this.type} ${this.objString(updateItem)}\n${err}`
              );
            });
        },
      })
      .promise();
  }
}

import log from '../logger';
import APIHandler from '../tools/auth0/handlers/default';
import { Asset, CalculatedChanges } from '../types';

/**
 * @template T
 * @param {typeof import('./auth0/handlers/default').default} handler
 * @param {T} desiredAssetState
 * @param {T} currentAssetState
 * @param {string[]} [objectFields=[]]
 * @param {boolean} [allowDelete]
 * @returns T
 */
export function processChangedObjectFields({
  handler,
  desiredAssetState,
  currentAssetState,
  allowDelete,
}: {
  handler: APIHandler;
  desiredAssetState: Asset;
  currentAssetState: Asset;
  allowDelete: boolean;
}) {
  const desiredAssetStateWithChanges = { ...desiredAssetState };

  // eslint-disable-next-line no-restricted-syntax
  for (const fieldName of handler.objectFields) {
    const areDesiredStateAndCurrentStateEmpty =
      Object.keys(desiredAssetState[fieldName] || {}).length === 0 &&
      Object.keys(currentAssetState[fieldName] || {}).length === 0;
    if (areDesiredStateAndCurrentStateEmpty) {
      // If both the desired state and current state for a given object is empty, it is a no-op and can skip
      // eslint-disable-next-line no-continue
      continue;
    }

    // A desired state that omits the objectField OR that has it as an empty object should
    // signal that all fields should be removed (subject to ALLOW_DELETE).
    if (desiredAssetState[fieldName] && Object.keys(desiredAssetState[fieldName]).length) {
      // Both the current and desired state have the object field. Here's where we need to map
      // to the APIv2 protocol of setting `null` values for deleted fields.
      // For new and modified properties of the object field, we can just pass them through to
      // APIv2.
      if (currentAssetState[fieldName]) {
        // eslint-disable-next-line no-restricted-syntax
        for (const currentObjectFieldPropertyName of Object.keys(currentAssetState[fieldName])) {
          // Loop through each object property that exists currently
          if (desiredAssetState[fieldName][currentObjectFieldPropertyName] === undefined) {
            // If the object has a property that exists now but doesn't exist in the proposed state
            if (allowDelete) {
              desiredAssetStateWithChanges[fieldName][currentObjectFieldPropertyName] = null;
            } else {
              // If deletes aren't allowed, do outright delete the property within the object
              log.warn(
                `Detected that the ${fieldName} of the following ${
                  handler.name || handler.id || ''
                } should be deleted. Doing so may be destructive.\nYou can enable deletes by setting 'AUTH0_ALLOW_DELETE' to true in the config\n${handler.objString(
                  currentAssetState
                )}`
              );
            }
          }
        }
      }
    } else if (allowDelete) {
      // If the desired state does not have the object field and the current state does, we
      // should mark *all* properties for deletion by specifying an empty object.
      //
      // See: https://auth0.com/docs/users/metadata/manage-metadata-api#delete-user-metadata
      desiredAssetStateWithChanges[fieldName] = {};
    } else {
      delete desiredAssetStateWithChanges[fieldName];
      log.warn(
        `Detected that the ${fieldName} of the following ${
          handler.name || handler.id || ''
        } should be emptied. Doing so may be destructive.\nYou can enable deletes by setting 'AUTH0_ALLOW_DELETE' to true in the config\n${handler.objString(
          currentAssetState
        )}`
      );
    }
  }

  return desiredAssetStateWithChanges;
}

export function calculateChanges({
  handler,
  assets,
  existing,
  identifiers = ['id', 'name'],
  allowDelete,
}: {
  handler: APIHandler;
  assets: Asset[];
  existing: Asset[];
  identifiers: string[];
  allowDelete: boolean;
}): CalculatedChanges {
  // Calculate the changes required between two sets of assets.
  const update: Asset[] = [];
  let del: Asset[] = [...existing];
  let create: Asset[] = [...assets];
  const conflicts: Asset[] = [];

  const findByKeyValue = (key: string, value: string, arr: Asset[]): Asset | undefined =>
    arr.find((e) => {
      if (Array.isArray(key)) {
        const values = key.map((k) => e[k]);
        if (values.every((v) => v)) {
          return value === values.join('-');
        }
      }
      return e[key] === value;
    });

  const processAssets = (id: string, arr: Asset[]) => {
    arr.forEach((asset) => {
      const assetIdValue: string | undefined = (() => {
        if (Array.isArray(id)) {
          const values = id.map((i) => asset[i]);
          if (values.every((v) => v)) {
            return values.join('-');
          }
        }

        return asset[id] as string;
      })();

      if (assetIdValue !== undefined) {
        const found = findByKeyValue(id, assetIdValue, del);
        if (found !== undefined) {
          // Delete from existing
          del = del.filter((e) => e !== found);

          // Delete from create as it's an update
          create = create.filter((e) => e !== asset);

          // Append identifiers to asset
          update.push({
            ...identifiers.reduce((obj, i) => {
              if (found[i]) obj[i] = found[i];
              return obj;
            }, {}),
            // If we have any object fields, we need to make sure that they get
            // special treatment. When different metadata objects are passed to APIv2
            // properties must explicitly be marked for deletion by indicating a `null`
            // value.
            ...(handler.objectFields.length
              ? processChangedObjectFields({
                  handler,
                  desiredAssetState: asset,
                  currentAssetState: found,
                  allowDelete,
                })
              : asset),
          });
        }
      }
    });
  };

  // Loop through identifiers (in order) to try match assets to existing
  // If existing then update if not create
  // The remainder will be deleted
  for (const id of identifiers) {
    // eslint-disable-line
    processAssets(id, [...create]);
  }

  // Check if there are assets with names that will conflict with existing names during the update process
  // This will rename those assets to a temp random name first
  // This assumes the first identifiers is the unique identifier
  if (identifiers.includes('name')) {
    const uniqueID = identifiers[0];
    const futureAssets: Asset[] = [...create, ...update];
    futureAssets.forEach((a) => {
      // If the conflicting item is going to be deleted then skip
      const inDeleted = del.filter((e) => e.name === a.name && e[uniqueID] !== a[uniqueID])[0];
      if (!inDeleted) {
        const conflict = existing.filter(
          (e) => e.name === a.name && e[uniqueID] !== a[uniqueID]
        )[0];
        if (conflict) {
          const temp = Math.random().toString(36).substr(2, 5);
          conflicts.push({
            ...conflict,
            name: `${conflict.name}-${temp}`,
          });
        }
      }
    });
  }

  return {
    del,
    update,
    conflicts,
    create,
  };
}

import { Assets, Asset, CalculatedChanges } from '../../../types';
import DefaultHandler from './default';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      key: { type: 'string', pattern: '^[A-Za-z0-9_-]*$' },
      value: { type: 'string' },
    },
    required: ['key', 'value'],
  },
  additionalProperties: false,
};

export default class RulesConfigsHandler extends DefaultHandler {
  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'rulesConfigs',
      id: 'key',
      functions: {
        update: 'set', // Update or Creation of a ruleConfig is via set not update
      },
    });
  }

  async getType(): Promise<Asset[]> {
    return this.client.rulesConfigs.getAll();
  }

  objString(item): string {
    return super.objString({ key: item.key });
  }

  async calcChanges(assets: Assets): Promise<CalculatedChanges> {
    const { rulesConfigs } = assets;

    // Do nothing if not set
    if (!rulesConfigs || !rulesConfigs.length)
      return {
        del: [],
        update: [],
        create: [],
        conflicts: [],
      };

    // Intention is to not delete/cleanup old configRules, that needs to be handled manually.
    return {
      del: [],
      update: rulesConfigs,
      create: [],
      conflicts: [],
    };
  }
}

import DefaultHandler from './default';
import constants from '../../constants';
import { Asset, Assets, CalculatedChanges } from '../../../types';

const ALLOWED_TRIGGER_IDS = [
  'credentials-exchange',
  'pre-user-registration',
  'post-user-registration',
  'post-change-password',
  'send-phone-message',
];

export const excludeSchema = {
  type: 'array',
  items: { type: 'string' },
};

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    default: [],
    properties: {
      script: {
        type: 'string',
        description: "A script that contains the hook's code",
        default: '',
      },
      name: {
        type: 'string',
        description:
          "The name of the hook. Can only contain alphanumeric characters, spaces and '-'. Can neither start nor end with '-' or spaces",
        pattern: '^[^-\\s][a-zA-Z0-9-\\s]+[^-\\s]$',
      },
      enabled: {
        type: 'boolean',
        description: 'true if the hook is active, false otherwise',
        default: false,
      },
      triggerId: {
        type: 'string',
        description: "The hooks's trigger ID",
        enum: ALLOWED_TRIGGER_IDS,
      },
      secrets: {
        type: 'object',
        description: 'List of key-value pairs containing secrets available to the hook.',
        default: {},
      },
      dependencies: {
        type: 'object',
        default: {},
        description: 'List of key-value pairs of NPM dependencies available to the hook.',
      },
    },
    required: ['script', 'name', 'triggerId'],
  },
};

const getCertainHook = (hooks: Asset[], name: string, triggerId: string): Asset | null => {
  let result: Asset | null = null;

  hooks.forEach((hook) => {
    if (hook.name === name && hook.triggerId === triggerId) {
      result = hook;
    }
  });

  return result;
};

const getActive = (hooks) => {
  const result = {};

  ALLOWED_TRIGGER_IDS.forEach((type) => {
    result[type] = hooks.filter((h) => h.active && h.triggerId === type);
  });

  return result;
};

export default class HooksHandler extends DefaultHandler {
  existing: Asset[];

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'hooks',
      stripUpdateFields: ['id', 'triggerId'],
    });
  }

  objString(hook): string {
    return super.objString({ name: hook.name, triggerId: hook.triggerId });
  }

  async processSecrets(hooks): Promise<void> {
    const allHooks = await this.getType(true);
    const changes: CalculatedChanges = {
      create: [],
      update: [],
      del: [],
      conflicts: [],
    };

    hooks.forEach((hook) => {
      const current = getCertainHook(allHooks, hook.name, hook.triggerId);
      if (current) {
        // if the hook was deleted we don't care about its secrets
        const oldSecrets = current.secrets || {};
        const newSecrets = hook.secrets || {};
        const create = {};
        const update = {};
        const del: string[] = [];

        Object.keys(newSecrets).forEach((key) => {
          if (!oldSecrets[key]) {
            create[key] = newSecrets[key];
          } else if (newSecrets[key] !== constants.HOOKS_HIDDEN_SECRET_VALUE) {
            update[key] = newSecrets[key];
          }
        });

        Object.keys(oldSecrets).forEach((key) => {
          if (!newSecrets[key]) {
            del.push(key);
          }
        });

        if (Object.keys(create).length)
          changes.create.push({ hookId: current.id, secrets: create });
        if (Object.keys(update).length)
          changes.update.push({ hookId: current.id, secrets: update });
        if (del.length) changes.del.push({ hookId: current.id, secrets: del });
      }
    });

    await Promise.all(
      changes.del.map(async (data) => {
        await this.client.hooks.removeSecrets({ id: data.hookId }, data.secrets);
      })
    );

    await Promise.all(
      changes.update.map(async (data) => {
        await this.client.hooks.updateSecrets({ id: data.hookId }, data.secrets);
      })
    );

    await Promise.all(
      changes.create.map(async (data) => {
        await this.client.hooks.addSecrets({ id: data.hookId }, data.secrets);
      })
    );
  }

  //@ts-ignore because hooks use a special reload argument
  async getType(reload: boolean): Promise<Asset[]> {
    if (this.existing && !reload) {
      return this.existing;
    }

    // in case client version does not support hooks
    if (!this.client.hooks || typeof this.client.hooks.getAll !== 'function') {
      return [];
    }

    try {
      const hooks = await this.client.hooks.getAll({ paginate: true, include_totals: true });

      // hooks.getAll does not return code and secrets, we have to fetch hooks one-by-one
      this.existing = await Promise.all(
        hooks.map((hook: { id: string }) =>
          this.client.hooks
            .get({ id: hook.id })
            .then((hookWithCode) =>
              this.client.hooks
                .getSecrets({ id: hook.id })
                .then((secrets) => ({ ...hookWithCode, secrets }))
            )
        )
      );

      return this.existing;
    } catch (err) {
      if (err.statusCode === 404 || err.statusCode === 501) {
        return [];
      }
      throw err;
    }
  }

  async calcChanges(assets: Assets): Promise<CalculatedChanges> {
    const { del, update, create, conflicts } = await super.calcChanges(assets);

    // strip secrets before hooks creating/updating, secrets have to be handled separately
    const stripSecrets = (list) => list.map((item) => ({ ...item, secrets: undefined }));

    return {
      del,
      update: stripSecrets(update),
      create: stripSecrets(create),
      conflicts: stripSecrets(conflicts),
    };
  }

  async validate(assets): Promise<void> {
    const { hooks } = assets;

    // Do nothing if not set
    if (!hooks) return;

    const activeHooks = getActive(hooks);

    ALLOWED_TRIGGER_IDS.forEach((type) => {
      if (activeHooks[type].length > 1) {
        // There can be only one!
        const conflict = activeHooks[type].map((h) => h.name).join(', ');
        const err = new Error(
          `Only one active hook allowed for "${type}" extensibility point. Conflicting hooks: ${conflict}`
        );
        //@ts-ignore need to investigate if appending status actually works here
        err.status = 409;
        throw err;
      }
    });

    await super.validate(assets);
  }

  async processChanges(assets: Assets): Promise<void> {
    const { hooks } = assets;

    // Do nothing if not set
    if (!hooks) return;

    // Figure out what needs to be updated vs created
    const changes = await this.calcChanges(assets);
    await super.processChanges(assets, {
      del: changes.del,
      create: changes.create,
      update: changes.update,
      conflicts: changes.conflicts,
    });

    await this.processSecrets(hooks);
  }
}

import DefaultHandler from './default';
import constants from '../../constants';
import { Asset, Assets } from '../../../types';

export const supportedPages = constants.PAGE_NAMES.filter((p) => p.includes('.json')).map((p) =>
  p.replace('.json', '')
);

export const pageNameMap = {
  guardian_multifactor: 'guardian_mfa_page',
  password_reset: 'change_password',
  error_page: 'error_page',
};

export type Page = {
  show_log_link?: boolean;
  name: string;
  enabled?: boolean;
  html?: string;
  url?: string;
};

// With this schema, we can only validate property types but not valid properties on per type basis
export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      name: { type: 'string', enum: supportedPages },
      html: { type: 'string', default: '' },
      url: { type: 'string' },
      show_log_link: { type: 'boolean' },
      enabled: { type: 'boolean' },
    },
    required: ['name'],
  },
};

export default class PagesHandler extends DefaultHandler {
  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'pages',
    });
  }

  objString(page): string {
    return super.objString({ name: page.name, enabled: page.enabled });
  }

  async updateLoginPage(page): Promise<void> {
    const globalClient = await this.client.clients.getAll({
      is_global: true,
      paginate: true,
      include_totals: true,
    });

    if (!globalClient[0]) {
      throw new Error('Unable to find global client id when trying to update the login page');
    }

    await this.client.clients.update(
      { client_id: globalClient[0].client_id },
      {
        custom_login_page: page.html,
        custom_login_page_on: page.enabled,
      }
    );
    this.updated += 1;
    this.didUpdate(page);
  }

  async updatePages(pages: Asset[]): Promise<void> {
    const toUpdate = pages.filter((p) => supportedPages.includes(p.name));
    const update = toUpdate.reduce((accum, page) => {
      if (supportedPages.includes(page.name)) {
        const pageName = pageNameMap[page.name];
        if (!pageName) {
          throw new Error(`Unable to map page ${page.name} into tenant level page setting`);
        }
        accum[pageName] = { ...page };
        delete accum[pageName].name;
      }
      return accum;
    }, {});

    if (Object.keys(update).length) {
      await this.client.tenant.updateSettings(update);
    }

    toUpdate.forEach((page) => {
      this.updated += 1;
      this.didUpdate(page);
    });
  }

  async getType(): Promise<Asset[]> {
    const pages: {
      name: string;
      enabled: boolean;
      html: string;
    }[] = [];

    // Login page is handled via the global client
    const globalClient = await this.client.clients.getAll({
      is_global: true,
      paginate: true,
      include_totals: true,
    });
    if (!globalClient[0]) {
      throw new Error('Unable to find global client id when trying to dump the login page');
    }

    if (globalClient[0].custom_login_page) {
      pages.push({
        name: 'login',
        enabled: globalClient[0].custom_login_page_on,
        html: globalClient[0].custom_login_page,
      });
    }

    const tenantSettings = await this.client.tenant.getSettings();

    Object.entries(pageNameMap).forEach(([key, name]) => {
      const page = tenantSettings[name];
      if (tenantSettings[name]) {
        pages.push({
          ...page,
          name: key,
        });
      }
    });

    return pages;
  }

  async processChanges(assets: Assets): Promise<void> {
    const { pages } = assets;

    // Do nothing if not set
    if (!pages) return;

    // Login page is handled via the global client
    const loginPage = pages.find((p) => p.name === 'login');
    if (loginPage !== undefined) {
      await this.updateLoginPage(loginPage);
    }

    // Rest of pages are on tenant level settings
    await this.updatePages(pages.filter((p) => p.name !== 'login'));
  }
}

import ValidationError from '../../validationError';

import constants from '../../constants';
import DefaultHandler from './default';
import { calculateChanges } from '../../calculateChanges';
import { Asset, Assets, CalculatedChanges } from '../../../types';

export const excludeSchema = {
  type: 'array',
  items: { type: 'string' },
};

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      name: { type: 'string' },
      identifier: { type: 'string' },
      scopes: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            name: { type: 'string' },
            description: { type: 'string' },
          },
        },
      },
      enforce_policies: { type: 'boolean' },
      token_dialect: { type: 'string' },
    },
    required: ['name', 'identifier'],
  },
};

export default class ResourceServersHandler extends DefaultHandler {
  existing: Asset[];

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'resourceServers',
      stripUpdateFields: ['identifier'], // Fields not allowed in updates
    });
  }

  objString(resourceServer): string {
    return super.objString({ name: resourceServer.name, identifier: resourceServer.identifier });
  }

  async getType(): Promise<Asset[]> {
    if (this.existing) return this.existing;
    const resourceServers = await this.client.resourceServers.getAll({
      paginate: true,
      include_totals: true,
    });
    return resourceServers.filter(
      (rs) => rs.name !== constants.RESOURCE_SERVERS_MANAGEMENT_API_NAME
    );
  }

  async calcChanges(assets: Assets): Promise<CalculatedChanges> {
    let { resourceServers } = assets;

    // Do nothing if not set
    if (!resourceServers)
      return {
        del: [],
        create: [],
        conflicts: [],
        update: [],
      };

    const excluded = (assets.exclude && assets.exclude.resourceServers) || [];

    let existing = await this.getType();

    // Filter excluded
    resourceServers = resourceServers.filter((r) => !excluded.includes(r.name));
    existing = existing.filter((r) => !excluded.includes(r.name));

    return calculateChanges({
      handler: this,
      assets: resourceServers,
      existing,
      identifiers: ['id', 'identifier'],
      allowDelete: !!this.config('AUTH0_ALLOW_DELETE'),
    });
  }

  async validate(assets: Assets): Promise<void> {
    const { resourceServers } = assets;

    // Do nothing if not set
    if (!resourceServers) return;

    const mgmtAPIResource = resourceServers.find(
      (r) => r.name === constants.RESOURCE_SERVERS_MANAGEMENT_API_NAME
    );
    if (mgmtAPIResource) {
      throw new ValidationError(
        `You can not configure the '${constants.RESOURCE_SERVERS_MANAGEMENT_API_NAME}'.`
      );
    }

    await super.validate(assets);
  }
}

import DefaultAPIHandler, { order } from './default';
import constants from '../../constants';
import { filterExcluded, getEnabledClients } from '../../utils';
import { CalculatedChanges, Assets } from '../../../types';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      strategy: { type: 'string', enum: ['auth0'], default: 'auth0' },
      name: { type: 'string' },
      options: {
        type: 'object',
        properties: {
          customScripts: {
            type: 'object',
            properties: {
              ...constants.DATABASE_SCRIPTS.reduce(
                (o, script) => ({ ...o, [script]: { type: 'string' } }),
                {}
              ),
            },
          },
        },
      },
    },
    required: ['name'],
  },
};

export default class DatabaseHandler extends DefaultAPIHandler {
  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'databases',
      stripUpdateFields: ['strategy', 'name'],
    });
  }

  objString(db) {
    return super.objString({ name: db.name, id: db.id });
  }

  getClientFN(fn: 'create' | 'delete' | 'getAll' | 'update'): Function {
    // Override this as a database is actually a connection but we are treating them as a different object
    // If we going to update database, we need to get current options first
    if (fn === 'update') {
      return (params, payload) =>
        this.client.connections.get(params).then((connection) => {
          payload.options = { ...connection.options, ...payload.options };
          return this.client.connections.update(params, payload);
        });
    }

    return this.client.connections[fn].bind(this.client.connections);
  }

  async getType() {
    if (this.existing) return this.existing;
    this.existing = this.client.connections.getAll({
      strategy: 'auth0',
      paginate: true,
      include_totals: true,
    });

    return this.existing;
  }

  async calcChanges(assets: Assets): Promise<CalculatedChanges> {
    const { databases } = assets;

    // Do nothing if not set
    if (!databases)
      return {
        del: [],
        create: [],
        update: [],
        conflicts: [],
      };

    // Convert enabled_clients by name to the id
    const clients = await this.client.clients.getAll({ paginate: true, include_totals: true });
    const existingDatabasesConnections = await this.client.connections.getAll({
      strategy: 'auth0',
      paginate: true,
      include_totals: true,
    });
    const formatted = databases.map((db) => {
      if (db.enabled_clients) {
        return {
          ...db,
          enabled_clients: getEnabledClients(assets, db, existingDatabasesConnections, clients),
        };
      }

      return db;
    });

    return super.calcChanges({ ...assets, databases: formatted });
  }

  // Run after clients are updated so we can convert all the enabled_clients names to id's
  @order('60')
  async processChanges(assets: Assets) {
    const { databases } = assets;

    // Do nothing if not set
    if (!databases) return;

    const excludedConnections: string[] = (assets.exclude && assets.exclude.databases) || [];

    const changes = await this.calcChanges(assets);

    await super.processChanges(assets, filterExcluded(changes, excludedConnections));
  }
}

import dotProp from 'dot-prop';
import _ from 'lodash';
import DefaultAPIHandler, { order } from './default';
import { filterExcluded, convertClientNameToId, getEnabledClients } from '../../utils';
import { CalculatedChanges, Asset, Assets } from '../../../types';
import { ConfigFunction } from '../../../configFactory';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      name: { type: 'string' },
      strategy: { type: 'string' },
      options: { type: 'object' },
      enabled_clients: { type: 'array', items: { type: 'string' } },
      realms: { type: 'array', items: { type: 'string' } },
      metadata: { type: 'object' },
    },
    required: ['name', 'strategy'],
  },
};

// addExcludedConnectionPropertiesToChanges superimposes excluded properties on the `options` object. The Auth0 API
// will overwrite the options property when updating connections, so it is necessary to add excluded properties back in to prevent those excluded properties from being deleted.
// This use case is common because organizations may not want to expose sensitive connection details, but want to preserve them in the tenant.
// exported only for unit testing purposes
export const addExcludedConnectionPropertiesToChanges = ({
  proposedChanges,
  existingConnections,
  config,
}: {
  proposedChanges: CalculatedChanges;
  existingConnections: Asset[];
  config: ConfigFunction;
}) => {
  if (proposedChanges.update.length === 0) return proposedChanges;

  //@ts-ignore because this expects a parameter to be passed
  const excludedFields = config()?.EXCLUDED_PROPS?.connections || [];
  if (excludedFields.length === 0) return proposedChanges;

  const existingConnectionsMap = _.keyBy(existingConnections, 'id');
  const excludedOptions = excludedFields.filter(
    // Only include fields that pertain to options
    (excludedField) => excludedField.startsWith('options')
  );

  const newProposedUpdates = proposedChanges.update.map((proposedConnection) => {
    const currConnection = existingConnectionsMap[proposedConnection.id];
    const currentExcludedPropertyValues = excludedOptions.reduce(
      (agg, excludedField) => {
        if (!dotProp.has(currConnection, excludedField)) return agg;

        const currentExcludedFieldValue = dotProp.get(currConnection, excludedField);

        dotProp.set(agg, excludedField, currentExcludedFieldValue);
        return agg;
      },
      {
        options: {},
      }
    );

    return {
      ...proposedConnection,
      options: {
        ...proposedConnection.options,
        ...currentExcludedPropertyValues.options,
      },
    };
  });

  return {
    ...proposedChanges,
    update: newProposedUpdates,
  };
};

export default class ConnectionsHandler extends DefaultAPIHandler {
  existing: Asset[] | null;

  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'connections',
      stripUpdateFields: ['strategy', 'name'],
    });
  }

  objString(connection): string {
    return super.objString({ name: connection.name, id: connection.id });
  }

  getFormattedOptions(connection, clients) {
    try {
      return {
        options: {
          ...connection.options,
          idpinitiated: {
            ...connection.options.idpinitiated,
            client_id: convertClientNameToId(connection.options.idpinitiated.client_id, clients),
          },
        },
      };
    } catch (e) {
      return {};
    }
  }

  async getType(): Promise<Asset[] | null> {
    if (this.existing) return this.existing;
    const connections: Asset[] = await this.client.connections.getAll({
      paginate: true,
      include_totals: true,
    });
    // Filter out database connections
    this.existing = connections.filter((c) => c.strategy !== 'auth0');
    if (this.existing === null) return [];
    return this.existing;
  }

  async calcChanges(assets: Assets): Promise<CalculatedChanges> {
    const { connections } = assets;

    // Do nothing if not set
    if (!connections)
      return {
        del: [],
        create: [],
        update: [],
        conflicts: [],
      };

    // Convert enabled_clients by name to the id
    const clients = await this.client.clients.getAll({ paginate: true, include_totals: true });
    const existingConnections = await this.client.connections.getAll({
      paginate: true,
      include_totals: true,
    });
    const formatted = connections.map((connection) => ({
      ...connection,
      ...this.getFormattedOptions(connection, clients),
      enabled_clients: getEnabledClients(assets, connection, existingConnections, clients),
    }));
    const proposedChanges = await super.calcChanges({ ...assets, connections: formatted });

    const proposedChangesWithExcludedProperties = addExcludedConnectionPropertiesToChanges({
      proposedChanges,
      existingConnections,
      config: this.config,
    });

    return proposedChangesWithExcludedProperties;
  }

  // Run after clients are updated so we can convert all the enabled_clients names to id's
  @order('60')
  async processChanges(assets: Assets): Promise<void> {
    const { connections } = assets;

    // Do nothing if not set
    if (!connections) return;

    const excludedConnections = (assets.exclude && assets.exclude.connections) || [];

    const changes = await this.calcChanges(assets);

    await super.processChanges(assets, filterExcluded(changes, excludedConnections));
  }
}

import { Asset, Assets } from '../../../types';
import DefaultAPIHandler from './default';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      name: { type: 'string', minLength: 1, pattern: '[^<>]+' },
    },
    required: ['name'],
  },
};

export default class ClientHandler extends DefaultAPIHandler {
  existing: Asset[] | null;

  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'clients',
      id: 'client_id',
      identifiers: ['client_id', 'name'],
      objectFields: ['client_metadata'],
      stripUpdateFields: [
        // Fields not allowed during updates
        'callback_url_template',
        'signing_keys',
        'global',
        'tenant',
        'jwt_configuration.secret_encoded',
      ],
    });
  }

  objString(item): string {
    return super.objString({ name: item.name, client_id: item.client_id });
  }

  async processChanges(assets: Assets): Promise<void> {
    const { clients } = assets;

    // Do nothing if not set
    if (!clients) return;

    const excludedClients = (assets.exclude && assets.exclude.clients) || [];

    const { del, update, create, conflicts } = await this.calcChanges(assets);

    // Always filter out the client we are using to access Auth0 Management API
    // As it could cause problems if it gets deleted or updated etc
    const currentClient = this.config('AUTH0_CLIENT_ID');

    const filterClients = (list) => {
      if (excludedClients.length) {
        return list.filter(
          (item) => item.client_id !== currentClient && !excludedClients.includes(item.name)
        );
      }

      return list.filter((item) => item.client_id !== currentClient);
    };

    const changes = {
      del: filterClients(del),
      update: filterClients(update),
      create: filterClients(create),
      conflicts: filterClients(conflicts),
    };

    await super.processChanges(assets, {
      ...changes,
    });
  }

  async getType() {
    if (this.existing) return this.existing;
    this.existing = await this.client.clients.getAll({
      paginate: true,
      include_totals: true,
      is_global: false,
    });
    return this.existing;
  }
}

import ValidationError from '../../validationError';

import DefaultHandler, { order } from './default';
import { supportedPages, pageNameMap } from './pages';
import { convertJsonToString } from '../../utils';
import { Asset, Assets, Language } from '../../../types';

export const schema = {
  type: 'object',
};

export type Tenant = Asset & { enabled_locales: Language[]; flags: { [key: string]: boolean } };

const blockPageKeys = [
  ...Object.keys(pageNameMap),
  ...Object.values(pageNameMap),
  ...supportedPages,
];

export default class TenantHandler extends DefaultHandler {
  existing: Tenant;

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'tenant',
    });
  }

  async getType(): Promise<Asset> {
    const tenant = await this.client.tenant.getSettings();

    this.existing = tenant;

    blockPageKeys.forEach((key) => {
      if (tenant[key]) delete tenant[key];
    });

    return tenant;
  }

  async validate(assets: Assets): Promise<void> {
    const { tenant } = assets;

    // Nothing to validate?
    if (!tenant) return;

    const pageKeys = Object.keys(tenant).filter((k) => blockPageKeys.includes(k));
    if (pageKeys.length > 0) {
      throw new ValidationError(
        `The following pages ${convertJsonToString(
          pageKeys
        )} were found in tenant settings. Pages should be set separately. Please refer to the documentation.`
      );
    }
  }

  // Run after other updates so objected can be referenced such as default directory
  @order('100')
  async processChanges(assets: Assets): Promise<void> {
    const { tenant } = assets;

    // Do nothing if not set
    if (!tenant) return;

    const existingTenant = this.existing || (await this.getType());

    const updatedTenant = {
      ...tenant,
      flags: sanitizeMigrationFlags({
        existingFlags: existingTenant.flags,
        proposedFlags: tenant.flags,
      }),
    };

    if (updatedTenant && Object.keys(updatedTenant).length > 0) {
      await this.client.tenant.updateSettings(updatedTenant);
      this.updated += 1;
      this.didUpdate(updatedTenant);
    }
  }
}

export const sanitizeMigrationFlags = ({
  existingFlags = {},
  proposedFlags = {},
}: {
  existingFlags: Tenant['flags'];
  proposedFlags: Tenant['flags'];
}): Tenant['flags'] => {
  /*
  Tenants can only update migration flags that are already configured.
  If moving configuration from one tenant to another, there may be instances
  where different migration flags exist and cause an error on update. This 
  function removes any migration flags that aren't already present on the target
  tenant. See: https://github.com/auth0/auth0-deploy-cli/issues/374
  */

  const tenantMigrationFlags = [
    'disable_clickjack_protection_headers',
    'enable_mgmt_api_v1',
    'trust_azure_adfs_email_verified_connection_property',
    'include_email_in_reset_pwd_redirect',
    'include_email_in_verify_email_redirect',
  ];

  return Object.keys(proposedFlags).reduce(
    (acc: Tenant['flags'], proposedKey: string): Tenant['flags'] => {
      const isMigrationFlag = tenantMigrationFlags.includes(proposedKey);
      if (!isMigrationFlag)
        return {
          ...acc,
          [proposedKey]: proposedFlags[proposedKey],
        };

      const keyCurrentlyExists = existingFlags[proposedKey] !== undefined;
      if (keyCurrentlyExists)
        return {
          ...acc,
          [proposedKey]: proposedFlags[proposedKey],
        };

      return acc;
    },
    {}
  );
};

import DefaultHandler from './default';
import { Asset, Assets } from '../../../types';
import logger from '../../../logger';

export const schema = { type: 'object' };

// The Management API requires the fields to be specified
const defaultFields = ['name', 'enabled', 'credentials', 'settings', 'default_from_address'];

export default class EmailProviderHandler extends DefaultHandler {
  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'emailProvider',
    });
  }

  async getType(): Promise<Asset> {
    try {
      return await this.client.emailProvider.get({ include_fields: true, fields: defaultFields });
    } catch (err) {
      if (err.statusCode === 404) return {};
      throw err;
    }
  }

  objString(provider) {
    return super.objString({ name: provider.name, enabled: provider.enabled });
  }

  async processChanges(assets: Assets): Promise<void> {
    const { emailProvider } = assets;

    if (!emailProvider) return;

    let existing = await this.getType();

    if (Object.keys(emailProvider).length === 0) {
      if (this.config('AUTH0_ALLOW_DELETE') === true) {
        logger.warn(
          'Setting email provider to empty object will delete the provider in future versions. The current inability to delete is considered a bug. For more details see: https://github.com/auth0/auth0-deploy-cli/issues/653'
        );
      }
      return;
    }

    if (existing.name) {
      if (existing.name !== emailProvider.name) {
        // Delete the current provider as it's different
        await this.client.emailProvider.delete();
        this.didDelete(existing);
        existing = {};
      }
    }

    if (existing.name) {
      const provider = { name: emailProvider.name, enabled: emailProvider.enabled };
      const updated = await this.client.emailProvider.update(provider, emailProvider);
      this.updated += 1;
      this.didUpdate(updated);
    } else {
      const provider = { name: emailProvider.name, enabled: emailProvider.enabled };
      const created = await this.client.emailProvider.configure(provider, emailProvider);
      this.created += 1;
      this.didCreate(created);
    }
  }
}

import DefaultHandler, { order } from './default';
import constants from '../../constants';
import { Assets, Asset } from '../../../types';

export const supportedTemplates = constants.EMAIL_TEMPLATES_NAMES.filter((p) =>
  p.includes('.json')
).map((p) => p.replace('.json', ''));

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      template: { type: 'string', enum: supportedTemplates },
      body: { type: 'string', default: '' },
    },
    required: ['template'],
  },
};

export default class EmailTemplateHandler extends DefaultHandler {
  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'emailTemplates',
      id: 'template',
    });
  }

  async getType(): Promise<Asset> {
    const emailTemplates = await Promise.all(
      constants.EMAIL_TEMPLATES_TYPES.map(async (name) => {
        try {
          const template = await this.client.emailTemplates.get({ name });
          return template;
        } catch (err) {
          // Ignore if not found, else throw error
          if (err.statusCode !== 404) {
            throw err;
          }
        }
      })
    );

    const nonEmptyTemplates = emailTemplates.filter((template) => !!template);

    return nonEmptyTemplates;
  }

  async updateOrCreate(emailTemplate): Promise<void> {
    try {
      const params = { name: emailTemplate[this.id] };
      const updated = await this.client.emailTemplates.update(params, emailTemplate);
      delete updated.body;
      this.didUpdate(updated);
      this.updated += 1;
    } catch (err) {
      if (err.statusCode === 404) {
        // Create if it does not exist
        const created = await this.client.emailTemplates.create(emailTemplate);
        delete created.body;
        this.didCreate(created);
        this.created += 1;
      } else {
        throw err;
      }
    }
  }

  // Run after email provider changes
  @order('60')
  async processChanges(assets: Assets): Promise<void> {
    const { emailTemplates } = assets;

    // Do nothing if not set
    if (!emailTemplates || !emailTemplates.length) return;

    await Promise.all(
      emailTemplates.map(async (emailTemplate) => {
        await this.updateOrCreate(emailTemplate);
      })
    );
  }
}

import DefaultHandler, { order } from './default';
import { convertClientNamesToIds } from '../../utils';
import { Asset, Assets, CalculatedChanges } from '../../../types';
import DefaultAPIHandler from './default';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      client_id: { type: 'string' },
      audience: { type: 'string' },
      scope: {
        type: 'array',
        items: { type: 'string' },
        uniqueItems: true,
      },
    },
    required: ['client_id', 'scope', 'audience'],
  },
};

export default class ClientGrantsHandler extends DefaultHandler {
  existing: Asset[] | null;

  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'clientGrants',
      id: 'id',
      //@ts-ignore because not sure why two-dimensional array passed in
      identifiers: ['id', ['client_id', 'audience']],
      stripUpdateFields: ['audience', 'client_id'],
    });
  }

  objString(item): string {
    return super.objString({ id: item.id, client_id: item.client_id, audience: item.audience });
  }

  async getType(): Promise<Asset> {
    if (this.existing) {
      return this.existing;
    }
    this.existing = await this.client.clientGrants.getAll({ paginate: true, include_totals: true });

    // Always filter out the client we are using to access Auth0 Management API
    // As it could cause problems if the grants are deleted or updated etc
    const currentClient = this.config('AUTH0_CLIENT_ID');

    this.existing = this.existing.filter((grant) => grant.client_id !== currentClient);

    return this.existing;
  }

  // Run after clients are updated so we can convert client_id names to id's
  @order('60')
  async processChanges(assets: Assets): Promise<void> {
    const { clientGrants } = assets;

    // Do nothing if not set
    if (!clientGrants) return;

    const clients = await this.client.clients.getAll({ paginate: true, include_totals: true });
    const excludedClientsByNames = (assets.exclude && assets.exclude.clients) || [];
    const excludedClients = convertClientNamesToIds(excludedClientsByNames, clients);

    // Convert clients by name to the id
    const formatted = clientGrants.map((clientGrant) => {
      const grant = { ...clientGrant };
      const found = clients.find((c) => c.name === grant.client_id);
      if (found) grant.client_id = found.client_id;
      return grant;
    });

    // Always filter out the client we are using to access Auth0 Management API
    const currentClient = this.config('AUTH0_CLIENT_ID');

    const { del, update, create, conflicts } = await this.calcChanges({
      ...assets,
      clientGrants: formatted,
    });

    const filterGrants = (list: { client_id: string }[]) => {
      if (excludedClients.length) {
        return list.filter(
          (item) =>
            item.client_id !== currentClient &&
            ![...excludedClientsByNames, ...excludedClients].includes(item.client_id)
        );
      }

      return list.filter((item) => item.client_id !== currentClient);
    };

    const changes: CalculatedChanges = {
      //@ts-ignore because this expects `client_id` and that's not yet typed on Asset
      del: filterGrants(del),
      //@ts-ignore because this expects `client_id` and that's not yet typed on Asset
      update: filterGrants(update),
      //@ts-ignore because this expects `client_id` and that's not yet typed on Asset
      create: filterGrants(create),
      //@ts-ignore because this expects `client_id` and that's not yet typed on Asset
      conflicts: filterGrants(conflicts),
    };

    await super.processChanges(assets, {
      ...changes,
    });
  }
}

