    await this.client.guardian.updatePhoneFactorMessageTypes(params, data);
    this.updated += 1;
    this.didUpdate(guardianPhoneFactorMessageTypes);
  }
}

import DefaultHandler, { order } from './default';
import { calculateChanges } from '../../calculateChanges';
import log from '../../../logger';
import { Asset, Assets, CalculatedChanges } from '../../../types';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      name: { type: 'string' },
      id: { type: 'string' },
      description: { type: 'string' },
      permissions: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            permission_name: { type: 'string' },
            resource_server_identifier: { type: 'string' },
          },
        },
      },
    },
    required: ['name'],
  },
};

export default class RolesHandler extends DefaultHandler {
  existing: Asset[];

  constructor(config: DefaultHandler) {
    super({
      ...config,
      type: 'roles',
      id: 'id',
      identifiers: ['name'],
    });
  }

  async createRole(data): Promise<Asset> {
    const role = { ...data };
    delete role.permissions;

    const created = await this.client.roles.create(role);

    if (typeof data.permissions !== 'undefined' && data.permissions.length > 0) {
      await this.client.roles.permissions.create(
        { id: created.id },
        { permissions: data.permissions }
      );
    }

    return created;
  }

  async createRoles(creates: CalculatedChanges['create']): Promise<void> {
    await this.client.pool
      .addEachTask({
        data: creates || [],
        generator: (item) =>
          this.createRole(item)
            .then((data) => {
              this.didCreate(data);
              this.created += 1;
            })
            .catch((err) => {
              throw new Error(`Problem creating ${this.type} ${this.objString(item)}\n${err}`);
            }),
      })
      .promise();
  }

  async deleteRole(data) {
    await this.client.roles.delete({ id: data.id });
  }

  async deleteRoles(dels: CalculatedChanges['del']): Promise<void> {
    if (
      this.config('AUTH0_ALLOW_DELETE') === 'true' ||
      this.config('AUTH0_ALLOW_DELETE') === true
    ) {
      await this.client.pool
        .addEachTask({
          data: dels || [],
          generator: (item) =>
            this.deleteRole(item)
              .then(() => {
                this.didDelete(item);
                this.deleted += 1;
              })
              .catch((err) => {
                throw new Error(`Problem deleting ${this.type} ${this.objString(item)}\n${err}`);
              }),
        })
        .promise();
    } else {
      log.warn(`Detected the following roles should be deleted. Doing so may be destructive.\nYou can enable deletes by setting 'AUTH0_ALLOW_DELETE' to true in the config
      \n${dels.map((i) => this.objString(i)).join('\n')}`);
    }
  }

  async updateRole(data, roles) {
    const existingRole = await roles.find(
      (roleDataForUpdate) => roleDataForUpdate.name === data.name
    );

    const params = { id: data.id };
    const newPermissions = data.permissions;

    delete data.permissions;
    delete data.id;

    await this.client.roles.update(params, data);

    if (typeof existingRole.permissions !== 'undefined' && existingRole.permissions.length > 0) {
      await this.client.roles.permissions.delete(params, { permissions: existingRole.permissions });
    }

    if (typeof newPermissions !== 'undefined' && newPermissions.length > 0) {
      await this.client.roles.permissions.create(params, { permissions: newPermissions });
    }

    return params;
  }

  async updateRoles(updates: CalculatedChanges['update'], roles) {
    await this.client.pool
      .addEachTask({
        data: updates || [],
        generator: (item) =>
          this.updateRole(item, roles)
            .then((data) => {
              this.didUpdate(data);
              this.updated += 1;
            })
            .catch((err) => {
              throw new Error(`Problem updating ${this.type} ${this.objString(item)}\n${err}`);
            }),
      })
      .promise();
  }

  async getType() {
    if (this.existing) {
      return this.existing;
    }

    // in case client version does not support roles
    if (!this.client.roles || typeof this.client.roles.getAll !== 'function') {
      return [];
    }

    try {
      const roles = await this.client.roles.getAll({ paginate: true, include_totals: true });
      for (let index = 0; index < roles.length; index++) {
        const permissions = await this.client.roles.permissions.getAll({
          paginate: true,
          include_totals: true,
          id: roles[index].id,
        });
        const strippedPerms = await Promise.all(
          permissions.map(async (permission) => {
            delete permission.resource_server_name;
            delete permission.description;
            return permission;
          })
        );
        roles[index].permissions = strippedPerms;
      }
      this.existing = roles;
      return this.existing;
    } catch (err) {
      if (err.statusCode === 404 || err.statusCode === 501) {
        return [];
      }
      throw err;
    }
  }

  @order('60')
  async processChanges(assets: Assets): Promise<void> {
    const { roles } = assets;
    // Do nothing if not set
    if (!roles) return;
    // Gets roles from destination tenant
    const existing = await this.getType();

    const changes = calculateChanges({
      handler: this,
      assets: roles,
      existing,
      identifiers: ['id', 'name'],
      allowDelete: !!this.config('AUTH0_ALLOW_DELETE'),
    });
    log.debug(
      `Start processChanges for roles [delete:${changes.del.length}] [update:${changes.update.length}], [create:${changes.create.length}]`
    );
    const myChanges = [
      { del: changes.del },
      { create: changes.create },
      { update: changes.update },
    ];
    await Promise.all(
      myChanges.map(async (change) => {
        switch (true) {
          case change.del && change.del.length > 0:
            if (change.del) await this.deleteRoles(change.del);
            break;
          case change.create && change.create.length > 0:
            await this.createRoles(changes.create); //TODO: fix this tho change.create
            break;
          case change.update && change.update.length > 0:
            if (change.update) await this.updateRoles(change.update, existing);
            break;
          default:
            break;
        }
      })
    );
  }
}

import DefaultHandler from './default';
import constants from '../../constants';
import log from '../../../logger';
import { Asset, Assets } from '../../../types';

export const schema = {
  type: 'object',
  properties: {
    templates: {
      type: 'array',
      items: {
        type: 'object',
        properties: {
          template: { type: 'string' },
          body: { type: 'string' },
        },
      },
    },
  },
};

export default class BrandingHandler extends DefaultHandler {
  existing: Asset;

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'branding',
    });
  }

  async getType(): Promise<Asset> {
    let branding: {
      templates?: {
        template: string;
        body: string;
      }[];
    } = {};

    try {
      // in case client version does not support branding
      if (this.client.branding && typeof this.client.branding.getSettings === 'function') {
        branding = await this.client.branding.getSettings();
      }

      // in case client version does not custom domains
      if (this.client.customDomains && typeof this.client.customDomains.getAll === 'function') {
        const customDomains = await this.client.customDomains.getAll();
        // templates are only supported if there's custom domains.
        if (customDomains && customDomains.length) {
          const payload = await this.client.branding.getUniversalLoginTemplate();
          branding.templates = [
            {
              template: constants.UNIVERSAL_LOGIN_TEMPLATE,
              body: payload.body,
            },
          ];
        }
      }

      return branding;
    } catch (err) {
      log.debug(`Error calling branding API, ${err.message}, status code: ${err.statusCode}`);
      if (err.statusCode === 403) return branding;
      if (err.statusCode === 404) return branding;
      if (err.statusCode === 501) return branding;
      throw err;
    }
  }

  async processChanges(assets: Assets) {
    if (!assets.branding) return;

    const { templates, ...brandingSettings } = assets.branding;

    if (brandingSettings.logo_url === '') {
      //Sometimes blank logo_url returned by API but is invalid on import. See: DXCDT-240
      delete brandingSettings.logo_url;
    }

    if (brandingSettings && Object.keys(brandingSettings).length) {
      await this.client.branding.updateSettings({}, brandingSettings);
      this.updated += 1;
      this.didUpdate(brandingSettings);
    }

    // handle templates
    if (templates && templates.length) {
      const unknownTemplates = templates
        .filter((t) => !constants.SUPPORTED_BRANDING_TEMPLATES.includes(t.template))
        .map((t) => t.template);
      if (unknownTemplates.length) {
        // throw a helpful warning for unknown templates, the context handlers are unaware of which are supported, that's all handled here.
        log.warn(
          `Found unknown branding template(s): ${unknownTemplates
            .join()
            .toString()}. Supported branding templates are: ${constants.SUPPORTED_BRANDING_TEMPLATES.join()}.`
        );
      }

      const templateDefinition = templates.find(
        (t) => t.template === constants.UNIVERSAL_LOGIN_TEMPLATE
      );
      if (templateDefinition && templateDefinition.body) {
        await this.client.branding.setUniversalLoginTemplate(
          {},
          { template: templateDefinition.body }
        );
        this.updated += 1;
        this.didUpdate(templates);
      }
    }
  }
}

