export default class MigrationsHandler extends DefaultHandler {
  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'migrations',
    });
  }

  //TODO: standardize empty object literal with more intentional empty indicator
  async getType(): Promise<Asset[] | {}> {
    try {
      const migrations = await this.client.migrations.getMigrations();
      return migrations.flags;
    } catch (err) {
      if (err.statusCode === 404) return {};
      throw err;
    }
  }

  // Run at the end since switching a flag will depend on other applying other changes
  @order('150')
  async processChanges(assets: Assets): Promise<void> {
    const { migrations } = assets;

    if (migrations && Object.keys(migrations).length > 0) {
      const flags = await this.removeUnavailableMigrations(migrations);

      if (Object.keys(flags).length > 0) {
        await this.client.migrations.updateMigrations({ flags });
        this.updated += 1;
        this.didUpdate(flags);
      }
    }
  }

  logUnavailableMigrations(ignoreUnavailableMigrations: boolean, ignoredMigrations: string[]) {
    if (ignoreUnavailableMigrations) {
      log.info(
        `The following migrations are not available '${ignoredMigrations.join(
          ','
        )}'. The migrations will be ignored because you have AUTH0_IGNORE_UNAVAILABLE_MIGRATIONS=true in your configuration.`
      );
    } else {
      log.warn(
        `The following disabled migrations are not available '${ignoredMigrations.join(
          ','
        )}'. The migrations will be ignored, remove the migrations to avoid future warnings.`
      );
    }
  }

  async removeUnavailableMigrations(migrations: Asset[]): Promise<Asset[]> {
    const flags = { ...migrations };
    const ignoreUnavailableMigrations = !!this.config('AUTH0_IGNORE_UNAVAILABLE_MIGRATIONS');
    const existingMigrations = await this.getType();
    const unavailableMigrations = Object.keys(flags).filter(
      (flag) => !(flag in existingMigrations)
    );
    const ignoredMigrations = unavailableMigrations.filter(
      (flag) => ignoreUnavailableMigrations || flags[flag] === false
    );

    if (ignoredMigrations.length > 0) {
      this.logUnavailableMigrations(ignoreUnavailableMigrations, ignoredMigrations);
      ignoredMigrations.forEach((flag) => delete flags[flag]);
    }
    return flags;
  }
}

import _ from 'lodash';
import DefaultAPIHandler, { order } from './default';
import log from '../../../logger';
import { areArraysEquals } from '../../utils';
import { Asset, Assets, CalculatedChanges } from '../../../types';

const MAX_ACTION_DEPLOY_RETRY_ATTEMPTS = 60; // 60 * 2s => 2 min timeout

export type Action = {
  id: string;
  name: string;
  created_at: string;
  updated_at: string;
  deployed?: boolean;
  supported_triggers: {
    id: string;
    version: string;
    status?: string;
  }[];
  code?: string;
  dependencies?: [];
  runtime?: string;
  status?: string;
  secrets?: {
    name: string;
    value: string;
  }[];
  all_changes_deployed?: boolean;
  installed_integration_id?: string;
  integration?: Object;
};

// With this schema, we can only validate property types but not valid properties on per type basis
export const schema = {
  type: 'array',
  items: {
    type: 'object',
    required: ['name', 'supported_triggers', 'code'],
    additionalProperties: false,
    properties: {
      code: { type: 'string', default: '' },
      runtime: { type: 'string' },
      dependencies: {
        type: 'array',
        items: {
          type: 'object',
          additionalProperties: false,
          properties: {
            name: { type: 'string' },
            version: { type: 'string' },
            registry_url: { type: 'string' },
          },
        },
      },
      secrets: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            name: { type: 'string' },
            value: { type: 'string' },
            updated_at: { type: 'string', format: 'date-time' },
          },
        },
      },
      name: { type: 'string', default: '' },
      supported_triggers: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            id: { type: 'string', default: '' },
            version: { type: 'string' },
            url: { type: 'string' },
          },
        },
      },
      deployed: { type: 'boolean' },
      status: { type: 'string' },
    },
  },
};

function sleep(ms) {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

function isActionsDisabled(err) {
  const errorBody = _.get(err, 'originalError.response.body') || {};

  return err.statusCode === 403 && errorBody.errorCode === 'feature_not_enabled';
}

export function isMarketplaceAction(action: Action): boolean {
  return !!action.integration;
}

export default class ActionHandler extends DefaultAPIHandler {
  existing: Action[] | null;

  constructor(options: DefaultAPIHandler) {
    super({
      ...options,
      type: 'actions',
      functions: {
        create: (action: Action) => this.createAction(action),
        delete: (action: Action) => this.deleteAction(action),
      },
      stripUpdateFields: ['deployed', 'status'],
    });
  }

  async createAction(action: Action) {
    // Strip the deployed flag
    const addAction = { ...action };
    delete addAction.deployed;
    delete addAction.status;
    const createdAction = await this.client.actions.create(addAction);

    // Add the action id so we can deploy it later
    action.id = createdAction.id;
    return createdAction;
  }

  async deleteAction(action: Action) {
    if (!this.client.actions || typeof this.client.actions.delete !== 'function') {
      return [];
    }
    return this.client.actions.delete({ id: action.id, force: true });
  }

  objString(action) {
    return super.objString({ id: action.id, name: action.name });
  }

  async deployActions(actions) {
    await this.client.pool
      .addEachTask({
        data: actions || [],
        generator: (action) =>
          this.deployAction(action)
            .then(() => {
              log.info(`Deployed [${this.type}]: ${this.objString(action)}`);
            })
            .catch((err) => {
              throw new Error(`Problem Deploying ${this.type} ${this.objString(action)}\n${err}`);
            }),
      })
      .promise();
  }

  async deployAction(action) {
    try {
      await this.client.actions.deploy({ id: action.id });
    } catch (err) {
      // Retry if pending build.
      if (err.message && err.message.includes("must be in the 'built' state")) {
        if (!action.retry_count) {
          log.info(`[${this.type}]: Waiting for build to complete ${this.objString(action)}`);
          action.retry_count = 1;
        }
        if (action.retry_count > MAX_ACTION_DEPLOY_RETRY_ATTEMPTS) {
          throw err;
        }
        await sleep(2000);
        action.retry_count += 1;
        await this.deployAction(action);
      } else {
        throw err;
      }
    }
  }

  async actionChanges(action, found) {
    const actionChanges: Asset = {};

    // if action is deployed, should compare against curren_version - calcDeployedVersionChanges method
    if (!action.deployed) {
      // name or secrets modifications are not supported yet
      if (action.code !== found.code) {
        actionChanges.code = action.code;
      }

      if (action.runtime !== found.runtime) {
        actionChanges.runtime = action.runtime;
      }

      if (!areArraysEquals(action.dependencies, found.dependencies)) {
        actionChanges.dependencies = action.dependencies;
      }
    }

    if (!areArraysEquals(action.supported_triggers, found.supported_triggers)) {
      actionChanges.supported_triggers = action.supported_triggers;
    }

    return actionChanges;
  }

  async getType(): Promise<Asset[] | null> {
    if (this.existing) return this.existing;

    if (!this.client.actions || typeof this.client.actions.getAll !== 'function') {
      return [];
    }
    // Actions API does not support include_totals param like the other paginate API's.
    // So we set it to false otherwise it will fail with "Additional properties not allowed: include_totals"
    try {
      const actions = await this.client.actions.getAll({ paginate: true });
      this.existing = actions;
      return actions;
    } catch (err) {
      if (err.statusCode === 404 || err.statusCode === 501) {
        return null;
      }

      if (isActionsDisabled(err)) {
        log.info('Skipping actions because it is not enabled.');
        return null;
      }

      throw err;
    }
  }

  @order('60')
  async processChanges(assets: Assets) {
    const { actions } = assets;

    // Do nothing if not set
    if (!actions) return;
    const changes = await this.calcChanges(assets);

    //Management of marketplace actions not currently supported, see ESD-23225.
    const changesWithMarketplaceActionsFiltered: CalculatedChanges = (() => {
      return {
        ...changes,
        del: changes.del.filter((action: Action) => !isMarketplaceAction(action)),
      };
    })();

    await super.processChanges(assets, changesWithMarketplaceActionsFiltered);

    const postProcessedActions = await (async () => {
      this.existing = null; //Clear the cache
      const actions = await this.getType();
      return actions;
    })();

    // Deploy actions
    const deployActions = [
      ...changes.create
        .filter((action) => action.deployed)
        .map((actionWithoutId) => {
          // Add IDs to just-created actions
          const actionId = postProcessedActions?.find((postProcessedAction) => {
            return postProcessedAction.name === actionWithoutId.name;
          })?.id;

          const actionWithId = {
            ...actionWithoutId,
            id: actionId,
          };
          return actionWithId;
        })
        .filter((action) => !!action.id),
      ...changes.update.filter((action) => action.deployed),
    ];

    await this.deployActions(deployActions);
  }
}

import _ from 'lodash';
import DefaultHandler, { order } from './default';
import constants from '../../constants';
import log from '../../../logger';
import { Assets } from '../../../types';

export const schema = {
  type: 'object',
  items: {
    type: 'object',
    additionalProperties: true,
    properties: {
      trigger_id: {
        type: 'object',
        properties: {
          action_name: { type: 'string', enum: constants.ACTIONS_TRIGGERS },
          display_name: { type: 'string', default: '' },
        },
      },
    },
  },
};

function isActionsDisabled(err): boolean {
  const errorBody = _.get(err, 'originalError.response.body') || {};

  return err.statusCode === 403 && errorBody.errorCode === 'feature_not_enabled';
}

export default class TriggersHandler extends DefaultHandler {
  existing: {
    [key: string]: {
      action_name: string;
      display_name: string;
    };
  };

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'triggers',
      id: 'name',
    });
  }

  async getType(): Promise<DefaultHandler['existing']> {
    if (this.existing) {
      return this.existing;
    }

    // in case client version does not support actions
    if (!this.client.actions || typeof this.client.actions.getAllTriggers !== 'function') {
      return [];
    }

    const triggerBindings = {};

    try {
      const res = await this.client.actions.getAllTriggers();
      const triggers: string[] = _(res.triggers).map('id').uniq().value();

      for (let i = 0; i < triggers.length; i++) {
        const triggerId = triggers[i];
        const { bindings } = await this.client.actions.getTriggerBindings({
          trigger_id: triggerId,
        });
        if (bindings.length > 0) {
          triggerBindings[triggerId] = bindings.map((binding) => ({
            action_name: binding.action.name,
            display_name: binding.display_name,
          }));
        }
      }

      this.existing = triggerBindings;
      return this.existing;
    } catch (err) {
      if (err.statusCode === 404 || err.statusCode === 501) {
        return [];
      }

      if (isActionsDisabled(err)) {
        log.info('Skipping triggers because Actions is not enabled.');
        return {};
      }

      throw err;
    }
  }

  @order('80')
  async processChanges(assets: Assets): Promise<void> {
    // No API to delete or create triggers, we can only update.
    const { triggers } = assets;

    // Do nothing if not set
    if (!triggers) return;

    // Process each trigger
    await Promise.all(
      Object.entries(triggers).map(async ([name, data]) => {
        const bindings = data.map((binding) => ({
          ref: {
            type: 'action_name',
            value: binding.action_name,
          },
          display_name: binding.display_name,
        }));
        await this.client.actions.updateTriggerBindings({ trigger_id: name }, { bindings });
        this.didUpdate({ trigger_id: name });
        this.updated += 1;
      })
    );
  }
}

import _ from 'lodash';
import DefaultHandler, { order } from './default';
import { calculateChanges } from '../../calculateChanges';
import log from '../../../logger';
import { Asset, Assets, CalculatedChanges } from '../../../types';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      name: { type: 'string' },
      display_name: { type: 'string' },
      branding: { type: 'object' },
      metadata: { type: 'object' },
      connections: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            connection_id: { type: 'string' },
            assign_membership_on_login: { type: 'boolean' },
          },
        },
      },
    },
    required: ['name'],
  },
};

export default class OrganizationsHandler extends DefaultHandler {
  existing: Asset[];

  constructor(config: DefaultHandler) {
    super({
      ...config,
      type: 'organizations',
      id: 'id',
      identifiers: ['name'],
    });
  }

  async deleteOrganization(org): Promise<void> {
    await this.client.organizations.delete({ id: org.id });
  }

  async deleteOrganizations(data): Promise<void> {
    if (
      this.config('AUTH0_ALLOW_DELETE') === 'true' ||
      this.config('AUTH0_ALLOW_DELETE') === true
    ) {
      await this.client.pool
        .addEachTask({
          data: data || [],
          generator: (item) =>
            this.deleteOrganization(item)
              .then(() => {
                this.didDelete(item);
                this.deleted += 1;
              })
              .catch((err) => {
                throw new Error(`Problem deleting ${this.type} ${this.objString(item)}\n${err}`);
              }),
        })
        .promise();
    } else {
      log.warn(`Detected the following organizations should be deleted. Doing so may be destructive.\nYou can enable deletes by setting 'AUTH0_ALLOW_DELETE' to true in the config
      \n${data.map((i) => this.objString(i)).join('\n')}`);
    }
  }

  async createOrganization(org): Promise<Asset> {
    const organization = { ...org };
    delete organization.connections;

    const created = await this.client.organizations.create(organization);

    if (typeof org.connections !== 'undefined' && org.connections.length > 0) {
      await Promise.all(
        org.connections.map((conn) =>
          this.client.organizations.addEnabledConnection({ id: created.id }, conn)
        )
      );
    }

    return created;
  }

  async createOrganizations(creates: CalculatedChanges['create']) {
    await this.client.pool
      .addEachTask({
        data: creates || [],
        generator: (item) =>
          this.createOrganization(item)
            .then((data) => {
              this.didCreate(data);
              this.created += 1;
            })
            .catch((err) => {
              throw new Error(`Problem creating ${this.type} ${this.objString(item)}\n${err}`);
            }),
      })
      .promise();
  }

  async updateOrganization(org, organizations) {
    const { connections: existingConnections } = await organizations.find(
      (orgToUpdate) => orgToUpdate.name === org.name
    );

    const params = { id: org.id };
    const { connections } = org;

    delete org.connections;
    delete org.name;
    delete org.id;

    await this.client.organizations.update(params, org);

    const connectionsToRemove = existingConnections.filter(
      (c) => !connections.find((x) => x.connection_id === c.connection_id)
    );
    const connectionsToAdd = connections.filter(
      (c) => !existingConnections.find((x) => x.connection_id === c.connection_id)
    );
    const connectionsToUpdate = connections.filter((c) =>
      existingConnections.find(
        (x) =>
          x.connection_id === c.connection_id &&
          x.assign_membership_on_login !== c.assign_membership_on_login
      )
    );

    // Handle updates first
    await Promise.all(
      connectionsToUpdate.map((conn) =>
        this.client.organizations
          .updateEnabledConnection(
            { connection_id: conn.connection_id, ...params },
            { assign_membership_on_login: conn.assign_membership_on_login }
          )
          .catch(() => {
            throw new Error(
              `Problem updating Enabled Connection ${conn.connection_id} for organizations ${params.id}`
            );
          })
      )
    );

    await Promise.all(
      connectionsToAdd.map((conn) =>
        this.client.organizations
          .addEnabledConnection(params, _.omit(conn, 'connection'))
          .catch(() => {
            throw new Error(
              `Problem adding Enabled Connection ${conn.connection_id} for organizations ${params.id}`
            );
          })
      )
    );

    await Promise.all(
      connectionsToRemove.map((conn) =>
        this.client.organizations
          .removeEnabledConnection({ connection_id: conn.connection_id, ...params })
          .catch(() => {
            throw new Error(
              `Problem removing Enabled Connection ${conn.connection_id} for organizations ${params.id}`
            );
          })
      )
    );

    return params;
  }

  async updateOrganizations(updates: CalculatedChanges['update'], orgs: Asset[]): Promise<void> {
    await this.client.pool
      .addEachTask({
        data: updates || [],
        generator: (item) =>
          this.updateOrganization(item, orgs)
            .then((data) => {
              this.didUpdate(data);
              this.updated += 1;
            })
            .catch((err) => {
              throw new Error(`Problem updating ${this.type} ${this.objString(item)}\n${err}`);
            }),
      })
      .promise();
  }

  async getType(): Promise<Asset[]> {
    if (this.existing) {
      return this.existing;
    }

    if (!this.client.organizations || typeof this.client.organizations.getAll !== 'function') {
      return [];
    }

    try {
      const organizations = await this.client.organizations.getAll({
        checkpoint: true,
        include_totals: true,
      });
      for (let index = 0; index < organizations.length; index++) {
        const connections = await this.client.organizations.connections.get({
          id: organizations[index].id,
        });
        organizations[index].connections = connections;
      }
      this.existing = organizations;
      return this.existing;
    } catch (err) {
      if (err.statusCode === 404 || err.statusCode === 501) {
        return [];
      }
      throw err;
    }
  }

  // Run after connections
  @order('70')
  async processChanges(assets: Assets): Promise<void> {
    const { organizations } = assets;
    // Do nothing if not set
    if (!organizations) return;
    // Gets organizations from destination tenant
    const existing = await this.getType();
    const existingConnections = await this.client.connections.getAll({
      paginate: true,
      include_totals: true,
    });

    // We need to get the connection ids for the names configured so we can link them together
    organizations.forEach((org) => {
      org.connections = (org.connections || [])
        .map((connection) => {
          const { name } = connection;
          delete connection.name;

          return {
            ...connection,
            connection_id: (existingConnections.find((c) => c.name === name) || {}).id,
          };
        })
        .filter((connection) => !!connection.connection_id);
    });

    const changes = calculateChanges({
      handler: this,
      assets: organizations,
      existing,
      identifiers: ['id', 'name'],
      allowDelete: !!this.config('AUTH0_ALLOW_DELETE'),
    });

    log.debug(
      `Start processChanges for organizations [delete:${changes.del.length}] [update:${changes.update.length}], [create:${changes.create.length}]`
    );

    const myChanges = [
      { del: changes.del },
      { create: changes.create },
      { update: changes.update },
    ];

    await Promise.all(
      myChanges.map(async (change) => {
        switch (true) {
          case change.del && change.del.length > 0:
            await this.deleteOrganizations(change.del);
            break;
          case change.create && change.create.length > 0:
            await this.createOrganizations(changes.create);
            break;
          case change.update && change.update.length > 0:
            if (change.update) await this.updateOrganizations(change.update, existing);
            break;
          default:
            break;
        }
      })
    );
  }
}

import DefaultAPIHandler from './default';
import { Asset, Assets } from '../../../types';

export const schema = {
  type: 'object',
  properties: {
    breachedPasswordDetection: {
      type: 'object',
    },
    bruteForceProtection: {
      type: 'object',
    },
    suspiciousIpThrottling: {
      type: 'object',
    },
  },
