export default class AttackProtectionHandler extends DefaultAPIHandler {
  existing: {
    breachedPasswordDetection: any;
    bruteForceProtection: any;
    suspiciousIpThrottling: any;
  } | null;

  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'attackProtection',
    });
  }

  objString(item: Asset): string {
    const objectString = (() => {
      let obj = {};
      if (item.breachedPasswordDetection?.enabled) {
        obj['breached-password-protection'] = {
          enabled: item.breachedPasswordDetection.enabled,
        };
      }
      if (item.bruteForceProtection?.enabled) {
        obj['brute-force-protection'] = {
          enabled: item.bruteForceProtection.enabled,
        };
      }
      if (item.suspiciousIpThrottling?.enabled) {
        obj['suspicious-ip-throttling'] = {
          enabled: item.suspiciousIpThrottling.enabled,
        };
      }
      return obj;
    })();

    return super.objString(objectString);
  }

  async getType(): Promise<Asset> {
    if (this.existing) {
      return this.existing;
    }

    const [breachedPasswordDetection, bruteForceProtection, suspiciousIpThrottling] =
      await Promise.all([
        this.client.attackProtection.getBreachedPasswordDetectionConfig(),
        this.client.attackProtection.getBruteForceConfig(),
        this.client.attackProtection.getSuspiciousIpThrottlingConfig(),
      ]);

    this.existing = {
      breachedPasswordDetection,
      bruteForceProtection,
      suspiciousIpThrottling,
    };

    return this.existing;
  }

  async processChanges(assets: Assets): Promise<void> {
    const { attackProtection } = assets;

    if (!attackProtection || !Object.keys(attackProtection).length) {
      return;
    }

    Promise.all([
      this.client.attackProtection.updateBreachedPasswordDetectionConfig(
        {},
        attackProtection.breachedPasswordDetection
      ),
      this.client.attackProtection.updateSuspiciousIpThrottlingConfig(
        {},
        attackProtection.suspiciousIpThrottling
      ),
      this.client.attackProtection.updateBruteForceConfig(
        {},
        attackProtection.bruteForceProtection
      ),
    ]);

    this.updated += 1;
    this.didUpdate(attackProtection);
  }
}

import DefaultAPIHandler from './default';
import { Asset, Assets } from '../../../types';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      id: { type: 'string' },
      type: { type: 'string' },
      name: { type: 'string' },
      status: { type: 'string', enum: ['active', 'paused', 'suspended'] },
      sink: { type: 'object' },
      filters: {
        type: 'array',
        items: {
          type: 'object',
        },
      },
    },
    required: ['name'],
  },
};

type LogStream = {
  type: 'eventbridge' | 'eventgrid' | 'datadog' | 'http' | 'splunk' | 'sumo';
  name: string;
  id: string;
  status: 'active' | 'suspended' | 'paused';
  sink?: {
    [key: string]: string | boolean;
  };
};

export default class LogStreamsHandler extends DefaultAPIHandler {
  existing: Asset[] | null;

  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'logStreams',
      stripUpdateFields: ['type'],
      stripCreateFields: ['status', 'sink.awsPartnerEventSource'],
      sensitiveFieldsToObfuscate: ['sink.httpAuthorization'],
    });
  }

  objString(item: Asset): string {
    return super.objString(item.name);
  }

  async getType(): Promise<Asset> {
    if (this.existing) {
      return this.existing;
    }

    const logStreams = await this.client.logStreams.getAll({ paginate: false });

    const nonSuspendedLogStreams = logStreams.filter(
      (logStream: LogStream) => logStream.status !== 'suspended'
    );

    this.existing = nonSuspendedLogStreams;

    return nonSuspendedLogStreams;
  }

  async processChanges(assets: Assets): Promise<void> {
    const { logStreams } = assets;
    // Do nothing if not set
    if (!logStreams) return;
    // Figure out what needs to be updated vs created
    const changes = await this.calcChanges(assets).then((changes) => {
      return {
        ...changes,
        update: changes.update.map((update: LogStream) => {
          if (update.type === 'eventbridge' || update.type === 'eventgrid') {
            delete update.sink;
          }
          return update;
        }),
      };
    });

    await super.processChanges(assets, changes);
  }
}

import DefaultAPIHandler from './default';
import { Asset, Assets } from '../../../types';

export const schema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      custom_domain_id: { type: 'string' },
      custom_client_ip_header: {
        type: 'string',
        nullable: true,
        enum: ['true-client-ip', 'cf-connecting-ip', 'x-forwarded-for', null],
      },
      domain: { type: 'string' },
      primary: { type: 'boolean' },
      status: { type: 'string', enum: ['pending_verification', 'ready', 'disabled', 'pending'] },
      type: { type: 'string', enum: ['auth0_managed_certs', 'self_managed_certs'] },
      verification: { type: 'object' },
    },
    required: ['domain', 'type'],
  },
};

export default class CustomDomainsHadnler extends DefaultAPIHandler {
  existing: Asset[] | null;

  constructor(config: DefaultAPIHandler) {
    super({
      ...config,
      type: 'customDomains',
      id: 'custom_domain_id',
      identifiers: ['domain'],
      stripCreateFields: ['status', 'primary', 'verification'],
      functions: {
        delete: (args) => this.client.customDomains.delete({ id: args.custom_domain_id }),
      },
    });
  }

  objString(item: Asset): string {
    return super.objString(item.domain);
  }

  async getType(): Promise<Asset | null> {
    try {
      if (this.existing) {
        return this.existing;
      }

      const customDomains = await this.client.customDomains.getAll({ paginate: false });

      this.existing = customDomains;

      return customDomains;
    } catch (err) {
      if (
        err.statusCode === 403 &&
        err.message ===
          'The account is not allowed to perform this operation, please contact our support team'
      ) {
        return null;
      }
      throw err;
    }
  }

  async processChanges(assets: Assets): Promise<void> {
    const { customDomains } = assets;

    if (!customDomains) return;
    const changes = await this.calcChanges(assets).then((changes) => {
      const changesWithoutUpdates = {
        ...changes,
        create: changes.create.map((customDomainToCreate) => {
          const newCustomDomain = { ...customDomainToCreate };
          if (customDomainToCreate.custom_client_ip_header === null) {
            delete newCustomDomain.custom_client_ip_header;
          }

          return newCustomDomain;
        }),
        delete: changes.del.map((deleteToMake) => {
          const deleteWithSDKCompatibleID = {
            ...deleteToMake,
            id: deleteToMake.custom_domain_id,
          };
          delete deleteWithSDKCompatibleID['custom_domain_id'];
          return deleteWithSDKCompatibleID;
        }),
        update: [], //Do not perform custom domain updates because not supported by SDK
      };
      return changesWithoutUpdates;
    });

    await super.processChanges(assets, changes);
  }
}

import { Assets } from '../../../types';
import log from '../../../logger';
import DefaultHandler from './default';

export default class ThemesHandler extends DefaultHandler {
  existing: Theme[] | null;

  constructor(options: DefaultHandler) {
    super({
      ...options,
      type: 'themes',
      id: 'themeId',
    });
  }

  objString(theme: Theme): string {
    return theme.displayName || JSON.stringify(theme);
  }

  async getType(): Promise<Theme[] | null> {
    if (!this.existing) {
      this.existing = await this.getThemes();
    }

    return this.existing;
  }

  async processChanges(assets: Assets): Promise<void> {
    const { themes } = assets;

    // Non existing section means themes doesn't need to be processed
    if (!themes) {
      return;
    }

    // Empty array means themes should be deleted
    if (themes.length === 0) {
      return this.deleteThemes();
    }

    return this.updateThemes(themes);
  }

  async deleteThemes(): Promise<void> {
    if (!this.config('AUTH0_ALLOW_DELETE')) {
      return;
    }

    // if theme exists we need to delete it
    const currentThemes = await this.getThemes();
    if (currentThemes === null || currentThemes.length === 0) {
      return;
    }

    const currentTheme = currentThemes[0];
    await this.client.branding.deleteTheme({ id: currentTheme.themeId });

    this.deleted += 1;
    this.didDelete(currentTheme);
  }

  async updateThemes(themes: Theme[]): Promise<void> {
    if (themes.length > 1) {
      log.warn('Only one theme is supported per tenant');
    }

    const currentThemes = await this.getThemes();

    const themeReqPayload = ((): Omit<Theme, 'themeId'> => {
      // Removing themeId from update and create payloads, otherwise API will error
      // Theme ID may be required to handle if `--export_ids=true`
      const payload = themes[0];
      //@ts-ignore to quell non-optional themeId property, but we know that it's ok to delete
      delete payload.themeId;
      return payload;
    })();

    if (currentThemes === null || currentThemes.length === 0) {
      await this.client.branding.createTheme(themeReqPayload);
    } else {
      const currentTheme = currentThemes[0];
      // if theme exists, overwrite it otherwise create it
      await this.client.branding.updateTheme({ id: currentTheme.themeId }, themeReqPayload);
    }

    this.updated += 1;
    this.didUpdate(themes[0]);
  }

  async getThemes(): Promise<Theme[] | null> {
    try {
      const theme = await this.client.branding.getDefaultTheme();
      return [theme];
    } catch (err) {
      if (err.statusCode === 404) return [];
      if (err.statusCode === 400) return null;

      // Errors other than 404 (theme doesn't exist) or 400 (no-code not enabled) shouldn't be expected
      throw err;
    }
  }
}

