import dotProp from 'dot-prop';
import _ from 'lodash';
import { Asset, Assets, AssetTypes, Config } from './types';

// Filter out known read only fields during dump
const readOnlyFields: Partial<Record<AssetTypes, string[]>> = {
  guardianFactors: ['trial_expired'],
  connections: ['provisioning_ticket_url', 'realms'],
  databases: ['options.configuration'],
  tenant: [
    'sandbox_version',
    'sandbox_versions_available',
    'flags.allow_changing_enable_sso',
    'flags.enable_sso',
    'flags.disable_impersonation',
    'flags.remove_stale_idp_attributes',
  ],
  clients: [
    'client_secret',
    'callback_url_template',
    'signing_keys',
    'global',
    'tenant',
    'custom_login_page_preview',
    'config_route',
    'owners',
  ],
};

function getExcludedFields(config: Config) {
  const strippedFields = { ...readOnlyFields };

  let { EXCLUDED_PROPS: excluded, INCLUDED_PROPS: included } = config;
  if (typeof excluded !== 'object') excluded = {};
  if (typeof included !== 'object') included = {};

  Object.entries(excluded).forEach(([name, fields]) => {
    // Do not allow same field to be included and excluded at the same time
    const intersections = fields.filter(
      (field) => included && included[name] && included[name].includes(field)
    );
    if (intersections.length > 0) {
      throw new Error(
        `EXCLUDED_PROPS should NOT have any intersections with INCLUDED_PROPS. Intersections found: ${name}: ${intersections.join(
          ', '
        )}`
      );
    }
    strippedFields[name] = (strippedFields[name] || []).concat(fields);
  });

  Object.entries(included).forEach(([name, fields]) => {
    if (strippedFields[name]) {
      strippedFields[name] = strippedFields[name].filter(
        (field: string) => !fields.includes(field)
      );
    }
  });

  return strippedFields;
}

function deleteKeys(obj: Asset, keys: string[]): { [key: string]: any } {
  const newObj = { ...obj };
  keys.forEach((k) => dotProp.delete(newObj, k));
  return newObj;
}

export default function cleanAssets(assets: Assets, config: Config): Assets {
  const cleaned = { ...assets };
  const excludedFields = getExcludedFields(config);

  Object.entries(excludedFields).forEach(([name, fields]: [AssetTypes, string[]]) => {
    const obj = cleaned[name];
    if (!obj) return;

    if (Array.isArray(obj)) {
      //@ts-ignore because `message_types` and `policies` on guardianPhoneFactorMessageTypes and guardianPolicies don't adhere to the expect types
      cleaned[name] = obj.map((o) => deleteKeys(o, fields));
    } else {
      //@ts-ignore because `message_types` and `policies` on guardianPhoneFactorMessageTypes and guardianPolicies don't adhere to the expect types
      cleaned[name] = deleteKeys(cleaned[name], fields);
    }
  });

  return _.pickBy(cleaned, _.identity) as Assets;
}

import * as path from 'path';
import { loadFileAndReplaceKeywords, Auth0 } from '../../tools';

import cleanAssets from '../../readonly';
import log from '../../logger';
import handlers, { DirectoryHandler } from './handlers';
import { isDirectory, isFile, stripIdentifiers, toConfigFn } from '../../utils';
import { Assets, Auth0APIClient, Config, AssetTypes } from '../../types';

type KeywordMappings = { [key: string]: (string | number)[] | string | number };

export default class DirectoryContext {
  basePath: string;
  filePath: string;
  config: Config;
  mappings: KeywordMappings;
  mgmtClient: Auth0APIClient;
  assets: Assets;

  constructor(config: Config, mgmtClient: Auth0APIClient) {
    this.filePath = config.AUTH0_INPUT_FILE;
    this.config = config;
    this.mappings = config.AUTH0_KEYWORD_REPLACE_MAPPINGS || {};
    this.mgmtClient = mgmtClient;

    //@ts-ignore for now
    this.assets = {};
    // Get excluded rules
    this.assets.exclude = {
      rules: config.AUTH0_EXCLUDED_RULES || [],
      clients: config.AUTH0_EXCLUDED_CLIENTS || [],
      databases: config.AUTH0_EXCLUDED_DATABASES || [],
      connections: config.AUTH0_EXCLUDED_CONNECTIONS || [],
      resourceServers: config.AUTH0_EXCLUDED_RESOURCE_SERVERS || [],
      defaults: config.AUTH0_EXCLUDED_DEFAULTS || [],
    };
  }

  loadFile(f: string, folder: string) {
    const basePath = path.join(this.filePath, folder);
    let toLoad = path.join(basePath, f);
    if (!isFile(toLoad)) {
      // try load not relative to yaml file
      toLoad = f;
    }
    return loadFileAndReplaceKeywords(toLoad, this.mappings);
  }

  async load(): Promise<void> {
    if (isDirectory(this.filePath)) {
      /* If this is a directory, look for each file in the directory */
      log.info(`Processing directory ${this.filePath}`);

      Object.entries(handlers)
        .filter(([handlerName]: [AssetTypes, DirectoryHandler<any>]) => {
          const excludedAssetTypes = this.config.AUTH0_EXCLUDED || [];
          return !excludedAssetTypes.includes(handlerName);
        })
        .forEach(([_name, handler]) => {
          const parsed = handler.parse(this);
          Object.entries(parsed).forEach(([k, v]) => {
            this.assets[k] = v;
          });
        });
      return;
    }
    throw new Error(`Not sure what to do with, ${this.filePath} as it is not a directory...`);
  }

  async dump(): Promise<void> {
    const auth0 = new Auth0(this.mgmtClient, this.assets, toConfigFn(this.config));
    log.info('Loading Auth0 Tenant Data');
    await auth0.loadAll();
    this.assets = auth0.assets;

    // Clean known read only fields
    this.assets = cleanAssets(this.assets, this.config);

    // Copy clients to be used by handlers which require converting client_id to the name
    // Must copy as the client_id will be stripped if AUTH0_EXPORT_IDENTIFIERS is false
    //@ts-ignore because assets haven't been typed yet TODO: type assets
    this.assets.clientsOrig = [...(this.assets.clients || [])];

    // Optionally Strip identifiers
    if (!this.config.AUTH0_EXPORT_IDENTIFIERS) {
      this.assets = stripIdentifiers(auth0, this.assets);
    }

    await Promise.all(
      Object.entries(handlers)
        .filter(([handlerName]: [AssetTypes, DirectoryHandler<any>]) => {
          const excludedAssetTypes = this.config.AUTH0_EXCLUDED || [];
          return !excludedAssetTypes.includes(handlerName);
        })
        .map(async ([name, handler]) => {
          try {
            await handler.dump(this);
          } catch (err) {
            log.debug(err.stack);
            throw new Error(`Problem exporting ${name}`);
          }
        })
    );
  }
}

