#!/usr/bin/env node
import { bootstrap } from 'global-agent';

import { getParams, CliParams } from './args';
import log from './logger';
import tools from './tools';
import { Stage } from './tools/auth0';

import importCMD from './commands/import';
import exportCMD from './commands/export';

async function run(params: CliParams): Promise<void> {
  // Run command
  const command = params._[0];

  const proxy = params.proxy_url;

  if (proxy) {
    const MAJOR_NODEJS_VERSION = parseInt(process.version.slice(1).split('.')[0], 10);

    if (MAJOR_NODEJS_VERSION < 10) {
      // `global-agent` works with Node.js v10 and above.
      throw new Error('The --proxy_url option is only supported on Node >= 10');
    }

    process.env.GLOBAL_AGENT_HTTP_PROXY = proxy;
    bootstrap();
  }

  log.debug(`Start command ${command}`);
  if (['deploy', 'import'].includes(command) && 'input_file' in params) {
    await importCMD(params);
  }
  if (['dump', 'export'].includes(command) && 'output_folder' in params) {
    await exportCMD(params);
  }
  log.debug(`Finished command ${command}`);
}

// Only run if from command line
if (require.main === module) {
  // Load cli params
  const params = getParams();

  log.debug('Starting Auth0 Deploy CLI Tool');

  if (params.debug) {
    log.level = 'debug';
    // Set for tools
    process.env.AUTH0_DEBUG = 'true';
    process.env.AUTH0_LOG = 'debug';
  }

  run(params)
    .then(() => process.exit(0))
    .catch((error: { type?: string; stage?: Stage; message?: string; stack?: string }) => {
      const command = params._[0];
      if (error.type || error.stage) {
        log.error(
          `Problem running command ${command} during stage ${error.stage} when processing type ${error.type}`
        );
      } else {
        log.error(`Problem running command ${command}`);
      }

      const msg = error.message || error.toString();
      log.error(msg);

      if (process.env.AUTH0_DEBUG === 'true' && error.stack) {
        log.debug(error.stack);
      }

      if (typeof msg === 'string' && msg.includes('Payload validation error')) {
        log.info(
          'Please refer to the Auth0 Management API docs for expected payloads: https://auth0.com/docs/api/management/v2'
        );
      }
      process.exit(1);
    });
}

// Export commands to be used programmatically
export default {
  deploy: importCMD,
  dump: exportCMD,
  import: importCMD,
  export: exportCMD,
  tools,
};

export const dump = exportCMD;
export const deploy = importCMD;

import yargs from 'yargs';
import { Config } from './types';

type SharedParams = {
  proxy_url?: string;
  debug?: boolean;
  config_file?: string;
  env?: boolean;
  secret?: string;
  base_path?: string; // Necessary when package imported as Node module
  config?: Partial<Config>;
};

type ImportSpecificParams = {
  input_file: string;
};

type ExportSpecificParams = {
  format: 'yaml' | 'directory';
  output_folder: string;
  export_ids?: boolean;
};

export type ExportParams = ExportSpecificParams & SharedParams;
export type ImportParams = ImportSpecificParams & SharedParams;

export type CliParams = (ExportParams | ImportParams) & {
  _: ['export' | 'import' | 'deploy' | 'dump'];
};

function getParams(): CliParams {
  const args = yargs
    .demandCommand(1, 'A command is required')
    .usage('Auth0 Deploy CLI')
    .option('debug', {
      alias: 'd',
      describe: 'Dump extra debug information.',
      type: 'boolean',
      default: false,
    })
    .option('proxy_url', {
      alias: 'p',
      describe: 'A url for proxying requests, only set this if you are behind a proxy.',
      type: 'string',
    })
    .command(['import', 'deploy'], 'Deploy Configuration', {
      input_file: {
        alias: 'i',
        describe:
          'The updates to deploy. Either a JSON file, or directory that contains the correct file layout. See README and online for more info.',
        type: 'string',
        demandOption: true,
      },
      config_file: {
        alias: 'c',
        describe: 'The JSON configuration file.',
        type: 'string',
      },
      env: {
        describe: 'Override the mappings in config with environment variables.',
        boolean: true,
        default: true,
      },
      secret: {
        alias: 'x',
        describe:
          'The client secret, this allows you to encrypt the secret in your build configuration instead of storing it in a config file',
        type: 'string',
      },
    })
    .command(['export', 'dump'], 'Export Auth0 Tenant Configuration', {
      output_folder: {
        alias: 'o',
        describe: 'The output directory.',
        type: 'string',
        demandOption: true,
      },
      format: {
        alias: 'f',
        describe: 'The output format.',
        type: 'string',
        choices: ['yaml', 'directory'],
        demandOption: true,
      },
      config_file: {
        alias: 'c',
        describe: 'The JSON configuration file.',
        type: 'string',
      },
      secret: {
        alias: 'x',
        describe:
          'The client secret, this allows you to encrypt the secret in your build configuration instead of storing it in a config file',
        type: 'string',
      },
      env: {
        describe: 'Override the mappings in config with environment variables.',
        boolean: true,
        default: true,
      },
      export_ids: {
        alias: 'e',
        describe: 'Export identifier field for each object type.',
        type: 'boolean',
        default: false,
      },
    })
    .example(
      '$0 export -c config.json -f yaml -o path/to/export',
      'Dump Auth0 config to folder in YAML format'
    )
    .example(
      '$0 export -c config.json -f directory -o path/to/export',
      'Dump Auth0 config to folder in directory format'
    )
    .example('$0 import -c config.json -i tenant.yaml', 'Deploy Auth0 via YAML')
    .example('$0 import -c config.json -i path/to/files', 'Deploy Auth0 via Path')
    .example(
      '$0 dump -c config.json -f yaml -o path/to/export',
      'Dump Auth0 config to folder in YAML format'
    )
    .example(
      '$0 dump -c config.json -f directory -o path/to/export',
      'Dump Auth0 config to folder in directory format'
    )
    .example('$0 deploy -c config.json -i tenant.yaml', 'Deploy Auth0 via YAML')
    .example('$0 deploy -c config.json -i path/to/files', 'Deploy Auth0 via Path')
    .epilogue(
      'See README (https://github.com/auth0/auth0-deploy-cli) for more in-depth information on configuration and setup.'
    )
    .wrap(null);
  //@ts-ignore because we know these types are either ExportParams or ImportParams. TODO: fix more native way of inferring types from yargs
  return args.argv;
}

