export { getParams };

import nconf from 'nconf';
import { configFactory } from '../configFactory';
import { deploy as toolsDeploy } from '../tools';
import log from '../logger';
import { setupContext } from '../context';
import { ImportParams } from '../args';

export default async function importCMD(params: ImportParams) {
  const {
    input_file: inputFile,
    base_path: basePath,
    config_file: configFile,
    config: configObj,
    env: shouldInheritEnv = false,
    secret: clientSecret,
  } = params;

  if (shouldInheritEnv) {
    nconf.env().use('memory');

    const mappings = nconf.get('AUTH0_KEYWORD_REPLACE_MAPPINGS') || {};
    nconf.set('AUTH0_KEYWORD_REPLACE_MAPPINGS', Object.assign(mappings, process.env));
  }

  if (configFile) {
    nconf.file(configFile);
  }

  const overrides = {
    AUTH0_INPUT_FILE: inputFile,
    AUTH0_BASE_PATH: basePath,
    AUTH0_KEYWORD_REPLACE_MAPPINGS: {},
    ...(configObj || {}),
  };

  // Prepare configuration by initializing nconf, then passing that as the provider to the config object
  // Allow passed in secret to override the configured one
  if (clientSecret) {
    overrides.AUTH0_CLIENT_SECRET = clientSecret;
  }

  nconf.overrides(overrides);

  // Setup context and load
  const context = await setupContext(nconf.get());
  await context.load();

  const config = configFactory();
  config.setProvider((key) => nconf.get(key));

  //@ts-ignore because context and assets still need to be typed TODO: type assets and type context
  await toolsDeploy(context.assets, context.mgmtClient, config);

  log.info('Import Successful');
}

import { Config } from './types';

export type ConfigFunction = (arg0: keyof Config) => any; // TODO: refactor configFactory function to allow for more precise typing

export const configFactory = () => {
  const settings: Partial<Config> = {};
  let currentProvider: ConfigFunction | null = null;

  const config = function getConfig(key: keyof Config) {
    if (settings && settings[key]) {
      return settings[key];
    }

    if (!currentProvider) {
      throw new Error('A configuration provider has not been set');
    }

    return currentProvider(key);
  };

  config.setProvider = function setProvider(providerFunction: ConfigFunction) {
    currentProvider = providerFunction;
  };

  config.setValue = function setValue(key: keyof Config, value: any) {
    settings[key] = value;
  };

  return config;
};

import path from 'path';
import nconf from 'nconf';
import mkdirp from 'mkdirp';

import log from '../logger';
import { isDirectory } from '../utils';
import { setupContext } from '../context/index';
import { Config } from '../types';
import { ExportParams } from '../args';

export default async function exportCMD(params: ExportParams) {
  const {
    output_folder: outputFolder,
    base_path: basePath,
    config_file: configFile,
    config: configObj,
    export_ids: exportIds,
    secret: clientSecret,
    env: shouldInheritEnv = false,
  } = params;

  if (shouldInheritEnv) {
    nconf.env().use('memory');
  }

  if (configFile) {
    nconf.file(configFile);
  }

  const overrides: Partial<Config> = {
    AUTH0_INPUT_FILE: outputFolder,
    AUTH0_BASE_PATH: basePath,
    ...(configObj || {}),
  };

  // Prepare configuration by initializing nconf, then passing that as the provider to the config object
  // Allow passed in secret to override the configured one
  if (clientSecret) {
    overrides.AUTH0_CLIENT_SECRET = clientSecret;
  }

  // Allow passed in export_ids to override the configured one
  if (exportIds) {
    overrides.AUTH0_EXPORT_IDENTIFIERS = exportIds;
  }

  // Check output folder
  if (!isDirectory(outputFolder)) {
    log.info(`Creating ${outputFolder}`);
    mkdirp.sync(outputFolder);
  }

  if (params.format === 'yaml') {
    overrides.AUTH0_INPUT_FILE = path.join(outputFolder, 'tenant.yaml');
  }

  nconf.overrides(overrides);

  // Setup context and load
  const context = await setupContext(nconf.get());
  await context.dump();
  log.info('Export Successful');
}
